import React, { useState, useEffect } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "../components/ui/card";
import { Button } from "../components/ui/button";
import { Badge } from "../components/ui/badge";
import { Input } from "../components/ui/input";
import {
  products,
  categories,
  searchProducts,
  getProductsByCategory,
  getProductsByDiet,
} from "../data/mockDatabase";
import { useCart } from "../contexts/CartContext";

export default function ProductsPage() {
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();
  const { addToCart, isInCart, getItemQuantity } = useCart();

  const [filteredProducts, setFilteredProducts] = useState(products);
  const [searchQuery, setSearchQuery] = useState(
    searchParams.get("search") || ""
  );
  const [selectedCategory, setSelectedCategory] = useState(
    searchParams.get("category") || "all"
  );
  const [selectedDiet, setSelectedDiet] = useState(
    searchParams.get("diet") || "all"
  );
  const [sortBy, setSortBy] = useState("name");

  useEffect(() => {
    filterProducts();
  }, [searchQuery, selectedCategory, selectedDiet, sortBy]);

  const filterProducts = () => {
    let filtered = [...products];

    // Search filter
    if (searchQuery) {
      filtered = searchProducts(searchQuery);
    }

    // Category filter
    if (selectedCategory !== "all") {
      const categoryId = parseInt(selectedCategory);
      filtered = filtered.filter(
        (product) => product.categoryId === categoryId
      );
    }

    // Diet filter
    if (selectedDiet !== "all") {
      filtered = filtered.filter((product) =>
        product.dietCompatible.includes(selectedDiet)
      );
    }

    // Sort
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "price-low":
          return a.price - b.price;
        case "price-high":
          return b.price - a.price;
        case "rating":
          return b.rating - a.rating;
        case "name":
        default:
          return a.name.localeCompare(b.name);
      }
    });

    setFilteredProducts(filtered);

    // Update URL params
    const params = new URLSearchParams();
    if (searchQuery) params.set("search", searchQuery);
    if (selectedCategory !== "all") params.set("category", selectedCategory);
    if (selectedDiet !== "all") params.set("diet", selectedDiet);
    setSearchParams(params);
  };

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  const dietOptions = [
    "Keto",
    "Vegan",
    "Vegetarian",
    "Paleo",
    "Mediterranean",
    "Gluten-Free",
  ];

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-gradient-to-r from-primary/10 to-secondary/10 py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h1 className="text-4xl font-bold text-foreground mb-4 font-heading">
              Fresh Products
            </h1>
            <p className="text-xl text-muted max-w-2xl mx-auto font-body">
              Discover our curated selection of fresh, healthy ingredients
              sourced from local farms and trusted suppliers.
            </p>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Filters Section */}
        <div className="mb-8 space-y-6">
          {/* Search Bar */}
          <div className="max-w-md">
            <Input
              type="text"
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full"
            />
          </div>

          {/* Filter Tabs */}
          <div className="flex flex-wrap gap-4">
            {/* Category Filter */}
            <div>
              <label className="block text-sm font-medium text-foreground mb-2 font-heading">
                Category
              </label>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-primary"
              >
                <option value="all">All Categories</option>
                {categories.map((category) => (
                  <option key={category.id} value={category.id}>
                    {category.icon} {category.name}
                  </option>
                ))}
              </select>
            </div>

            {/* Diet Filter */}
            <div>
              <label className="block text-sm font-medium text-foreground mb-2 font-heading">
                Diet Type
              </label>
              <select
                value={selectedDiet}
                onChange={(e) => setSelectedDiet(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-primary"
              >
                <option value="all">All Diets</option>
                {dietOptions.map((diet) => (
                  <option key={diet} value={diet}>
                    {diet}
                  </option>
                ))}
              </select>
            </div>

            {/* Sort Filter */}
            <div>
              <label className="block text-sm font-medium text-foreground mb-2 font-heading">
                Sort By
              </label>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-primary"
              >
                <option value="name">Name</option>
                <option value="price-low">Price: Low to High</option>
                <option value="price-high">Price: High to Low</option>
                <option value="rating">Rating</option>
              </select>
            </div>
          </div>
        </div>

        {/* Results Count */}
        <div className="mb-6">
          <p className="text-muted font-body">
            Showing {filteredProducts.length} product
            {filteredProducts.length !== 1 ? "s" : ""}
          </p>
        </div>

        {/* Products Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {filteredProducts.map((product) => (
            <Card
              key={product.id}
              className="group hover:shadow-xl transition-all duration-300 hover:-translate-y-2 cursor-pointer border-0 shadow-lg"
              onClick={() => handleProductClick(product.id)}
            >
              {/* Product Image */}
              <div className="relative p-6 bg-gradient-to-br from-gray-50 to-gray-100 rounded-t-2xl">
                <div className="text-6xl text-center mb-2">{product.image}</div>
                {product.organic && (
                  <Badge
                    variant="success"
                    className="absolute top-3 right-3 text-xs"
                  >
                    Organic
                  </Badge>
                )}
                {product.originalPrice > product.price && (
                  <Badge
                    variant="destructive"
                    className="absolute top-3 left-3 text-xs"
                  >
                    {Math.round(
                      ((product.originalPrice - product.price) /
                        product.originalPrice) *
                        100
                    )}
                    % OFF
                  </Badge>
                )}
              </div>

              <CardHeader className="pb-2">
                <div className="flex items-start justify-between">
                  <div>
                    <CardTitle className="text-lg font-bold text-foreground group-hover:text-primary transition-colors font-heading">
                      {product.name}
                    </CardTitle>
                    <CardDescription className="text-sm text-muted font-body">
                      {product.category} • {product.unit}
                    </CardDescription>
                  </div>
                </div>
              </CardHeader>

              <CardContent className="pt-0">
                {/* Rating */}
                <div className="flex items-center space-x-1 mb-3">
                  {[...Array(5)].map((_, i) => (
                    <svg
                      key={i}
                      className={`w-4 h-4 ${
                        i < Math.floor(product.rating)
                          ? "text-yellow-400"
                          : "text-gray-300"
                      }`}
                      fill="currentColor"
                      viewBox="0 0 20 20"
                    >
                      <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                    </svg>
                  ))}
                  <span className="text-sm text-muted ml-1">
                    ({product.reviews})
                  </span>
                </div>

                {/* Price */}
                <div className="flex items-center space-x-2 mb-4">
                  <span className="text-2xl font-bold text-primary">
                    ₹{product.price}
                  </span>
                  {product.originalPrice > product.price && (
                    <span className="text-sm text-gray-500 line-through">
                      ₹{product.originalPrice}
                    </span>
                  )}
                </div>

                {/* Diet Compatibility */}
                <div className="mb-4">
                  <div className="flex flex-wrap gap-1">
                    {product.dietCompatible.slice(0, 3).map((diet) => (
                      <Badge key={diet} variant="outline" className="text-xs">
                        {diet}
                      </Badge>
                    ))}
                    {product.dietCompatible.length > 3 && (
                      <Badge variant="outline" className="text-xs">
                        +{product.dietCompatible.length - 3}
                      </Badge>
                    )}
                  </div>
                </div>

                {/* Stock Status */}
                <div className="mb-4">
                  {product.inStock ? (
                    <span className="text-success text-sm font-medium">
                      ✓ In Stock
                    </span>
                  ) : (
                    <span className="text-destructive text-sm font-medium">
                      ✗ Out of Stock
                    </span>
                  )}
                </div>

                {/* Add to Cart Button */}
                <Button
                  className="w-full"
                  disabled={!product.inStock}
                  onClick={(e) => {
                    e.stopPropagation();
                    addToCart(product.id);
                  }}
                >
                  {!product.inStock
                    ? "Out of Stock"
                    : isInCart(product.id)
                    ? `In Cart (${getItemQuantity(product.id)})`
                    : "Add to Cart"}
                </Button>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* No Results */}
        {filteredProducts.length === 0 && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">🔍</div>
            <h3 className="text-xl font-bold text-foreground mb-2 font-heading">
              No products found
            </h3>
            <p className="text-muted font-body">
              Try adjusting your search or filter criteria
            </p>
          </div>
        )}
      </div>
    </div>
  );
}
