import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../contexts/CartContext";
import { Button } from "../components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "../components/ui/card";
import { Badge } from "../components/ui/badge";

export default function RecipesPage() {
  const navigate = useNavigate();
  const { cart } = useCart();

  const [searchQuery, setSearchQuery] = useState("");
  const [selectedDifficulty, setSelectedDifficulty] = useState("");
  const [selectedDietType, setSelectedDietType] = useState("");

  // Mock recipes database - same as RecipePage
  const recipes = [
    {
      name: "Quinoa Buddha Bowl",
      difficulty: "Easy",
      time: "25 min",
      servings: 2,
      calories: 420,
      image: "🥗",
      tags: ["Vegan", "High Protein", "Budget Friendly", "Gluten-Free"],
      description:
        "A nutritious and colorful bowl packed with protein, fiber, and essential nutrients.",
      ingredients: [
        { name: "Quinoa", quantity: "1 cup", productId: "quinoa-1kg" },
        {
          name: "Sweet potato",
          quantity: "1 medium",
          productId: "sweet-potato-500g",
        },
        { name: "Chickpeas", quantity: "1 can", productId: "chickpeas-can" },
        { name: "Kale", quantity: "2 cups", productId: "kale-bunch" },
        { name: "Avocado", quantity: "1", productId: "avocado" },
      ],
    },
    {
      name: "Mediterranean Salmon",
      difficulty: "Medium",
      time: "35 min",
      servings: 2,
      calories: 580,
      image: "🐟",
      tags: ["High Protein", "Omega-3", "Premium", "Low-Carb"],
      description:
        "A healthy and flavorful salmon dish with Mediterranean herbs and vegetables.",
      ingredients: [
        {
          name: "Salmon fillet",
          quantity: "2 pieces",
          productId: "salmon-fillet",
        },
        { name: "Olive oil", quantity: "3 tbsp", productId: "olive-oil" },
        { name: "Lemon", quantity: "1", productId: "lemon" },
        { name: "Fresh herbs", quantity: "1 bunch", productId: "fresh-herbs" },
        { name: "Quinoa", quantity: "1 cup", productId: "quinoa-1kg" },
      ],
    },
    {
      name: "Vegetarian Stir Fry",
      difficulty: "Easy",
      time: "20 min",
      servings: 3,
      calories: 320,
      image: "🥬",
      tags: ["Vegan", "Quick", "Budget Friendly", "High Fiber"],
      description:
        "A quick and healthy stir fry loaded with fresh vegetables and tofu.",
      ingredients: [
        { name: "Tofu", quantity: "200g", productId: null },
        { name: "Broccoli", quantity: "2 cups", productId: null },
        { name: "Carrots", quantity: "2 medium", productId: null },
        { name: "Soy sauce", quantity: "3 tbsp", productId: null },
        { name: "Garlic", quantity: "3 cloves", productId: "garlic" },
      ],
    },
    {
      name: "Chicken Quinoa Bowl",
      difficulty: "Medium",
      time: "30 min",
      servings: 2,
      calories: 450,
      image: "🍗",
      tags: ["High Protein", "Balanced", "Meal Prep", "Gluten-Free"],
      description:
        "A protein-rich bowl with grilled chicken, quinoa, and roasted vegetables.",
      ingredients: [
        { name: "Chicken breast", quantity: "2 pieces", productId: null },
        { name: "Quinoa", quantity: "1 cup", productId: "quinoa-1kg" },
        {
          name: "Sweet potato",
          quantity: "1 medium",
          productId: "sweet-potato-500g",
        },
        { name: "Olive oil", quantity: "2 tbsp", productId: "olive-oil" },
        { name: "Mixed herbs", quantity: "1 tbsp", productId: "fresh-herbs" },
      ],
    },
    {
      name: "Smoothie Bowl",
      difficulty: "Easy",
      time: "15 min",
      servings: 1,
      calories: 280,
      image: "🍓",
      tags: ["Vegan", "Breakfast", "Quick", "High Fiber"],
      description:
        "A refreshing and nutritious smoothie bowl perfect for breakfast.",
      ingredients: [
        { name: "Frozen berries", quantity: "1 cup", productId: null },
        { name: "Banana", quantity: "1 medium", productId: null },
        { name: "Almond milk", quantity: "1/2 cup", productId: null },
        { name: "Granola", quantity: "1/4 cup", productId: null },
        { name: "Chia seeds", quantity: "1 tbsp", productId: null },
      ],
    },
    {
      name: "Lentil Curry",
      difficulty: "Easy",
      time: "40 min",
      servings: 4,
      calories: 380,
      image: "🍛",
      tags: ["Vegan", "High Protein", "Budget Friendly", "Spicy"],
      description:
        "A hearty and flavorful lentil curry that's perfect for meal prep.",
      ingredients: [
        { name: "Red lentils", quantity: "1 cup", productId: null },
        { name: "Onion", quantity: "1 medium", productId: "onion" },
        { name: "Garlic", quantity: "4 cloves", productId: "garlic" },
        { name: "Coconut milk", quantity: "1 can", productId: null },
        { name: "Curry powder", quantity: "2 tbsp", productId: null },
      ],
    },
  ];

  const handleViewRecipe = (recipe) => {
    navigate(`/recipes/${encodeURIComponent(recipe.name)}`);
  };

  const filteredRecipes = recipes.filter((recipe) => {
    const matchesSearch =
      recipe.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      recipe.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
      recipe.tags.some((tag) =>
        tag.toLowerCase().includes(searchQuery.toLowerCase())
      );

    const matchesDifficulty =
      !selectedDifficulty || recipe.difficulty === selectedDifficulty;

    const matchesDietType =
      !selectedDietType || recipe.tags.includes(selectedDietType);

    return matchesSearch && matchesDifficulty && matchesDietType;
  });

  const difficultyOptions = [
    { value: "Easy", label: "Easy" },
    { value: "Medium", label: "Medium" },
    { value: "Hard", label: "Hard" },
  ];

  const dietTypeOptions = [
    { value: "Vegan", label: "Vegan" },
    { value: "High Protein", label: "High Protein" },
    { value: "Budget Friendly", label: "Budget Friendly" },
    { value: "Gluten-Free", label: "Gluten-Free" },
    { value: "Low-Carb", label: "Low-Carb" },
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header with Back Navigation */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => navigate("/")}
              className="text-gray-600 hover:text-green-600 transition-colors"
            >
              <svg
                className="w-6 h-6"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M10 19l-7-7m0 0l7-7m-7 7h18"
                />
              </svg>
            </button>
            <h1 className="text-2xl font-bold text-gray-900">
              Healthy Recipes
            </h1>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Filters */}
        <div className="mb-8 space-y-4">
          {/* Search */}
          <div className="max-w-md">
            <input
              type="text"
              placeholder="Search recipes..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
            />
          </div>

          {/* Difficulty and Diet Type Filters */}
          <div className="flex flex-wrap gap-4">
            <select
              value={selectedDifficulty}
              onChange={(e) => setSelectedDifficulty(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
            >
              <option value="">All Difficulties</option>
              {difficultyOptions.map((difficulty) => (
                <option key={difficulty.value} value={difficulty.value}>
                  {difficulty.label}
                </option>
              ))}
            </select>

            <select
              value={selectedDietType}
              onChange={(e) => setSelectedDietType(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
            >
              <option value="">All Diet Types</option>
              {dietTypeOptions.map((dietType) => (
                <option key={dietType.value} value={dietType.value}>
                  {dietType.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Recipes Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredRecipes.map((recipe, index) => (
            <Card
              key={index}
              className="group hover:shadow-xl transition-all duration-300 overflow-hidden cursor-pointer"
              onClick={() => handleViewRecipe(recipe)}
            >
              <div className="h-2 bg-gradient-to-r from-primary to-secondary"></div>

              <CardHeader className="pb-4">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <CardTitle className="text-xl font-bold text-foreground font-heading">
                      {recipe.name}
                    </CardTitle>
                    <CardDescription className="text-muted mt-2 font-body">
                      {recipe.description}
                    </CardDescription>
                  </div>
                  <div className="text-4xl ml-4">{recipe.image}</div>
                </div>

                {/* Recipe Meta */}
                <div className="flex items-center space-x-4 mt-4">
                  <Badge variant="outline" className="text-xs">
                    ⏱️ {recipe.time}
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    👥 {recipe.servings} servings
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    🔥 {recipe.calories} cal
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    📊 {recipe.difficulty}
                  </Badge>
                </div>

                {/* Tags */}
                <div className="flex flex-wrap gap-2 mt-3">
                  {recipe.tags.slice(0, 3).map((tag, idx) => (
                    <Badge key={idx} variant="secondary" className="text-xs">
                      {tag}
                    </Badge>
                  ))}
                  {recipe.tags.length > 3 && (
                    <Badge variant="outline" className="text-xs">
                      +{recipe.tags.length - 3} more
                    </Badge>
                  )}
                </div>
              </CardHeader>

              <CardContent className="pt-0">
                {/* Ingredients Preview */}
                <div className="mb-4">
                  <h4 className="font-semibold text-foreground mb-2 font-heading text-sm">
                    Key Ingredients:
                  </h4>
                  <div className="flex flex-wrap gap-1">
                    {recipe.ingredients.slice(0, 3).map((ingredient, idx) => (
                      <span
                        key={idx}
                        className="px-2 py-1 text-xs bg-gray-100 text-gray-700 rounded-full"
                      >
                        {ingredient.name}
                      </span>
                    ))}
                    {recipe.ingredients.length > 3 && (
                      <span className="px-2 py-1 text-xs bg-gray-100 text-gray-700 rounded-full">
                        +{recipe.ingredients.length - 3} more
                      </span>
                    )}
                  </div>
                </div>

                {/* View Recipe Button */}
                <Button
                  variant="gradient"
                  className="w-full"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleViewRecipe(recipe);
                  }}
                >
                  View Full Recipe
                </Button>
              </CardContent>
            </Card>
          ))}
        </div>

        {filteredRecipes.length === 0 && (
          <div className="text-center py-12">
            <p className="text-gray-500 text-lg">
              No recipes found matching your criteria.
            </p>
          </div>
        )}
      </div>
    </div>
  );
}
