import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useCart } from "../contexts/CartContext";
import { useAuth } from "../contexts/AuthContext";
import { Button } from "../components/ui/button";
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { toast } from "../components/ui/use-toast";

export default function RecipePage() {
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const { id } = useParams();
  const { addToCart, removeFromCart, updateQuantity, cart } = useCart();
  const { user } = useAuth();
  const navigate = useNavigate();

  // Mock products database - in real app this would come from API
  const productsDatabase = {
    "quinoa-1kg": {
      name: "Quinoa",
      price: 120,
      available: true,
      category: "Grains",
    },
    "sweet-potato-500g": {
      name: "Sweet potato",
      price: 40,
      available: true,
      category: "Vegetables",
    },
    "chickpeas-can": {
      name: "Chickpeas",
      price: 60,
      available: true,
      category: "Legumes",
    },
    "kale-bunch": {
      name: "Kale",
      price: 30,
      available: true,
      category: "Vegetables",
    },
    avocado: {
      name: "Avocado",
      price: 80,
      available: true,
      category: "Fruits",
    },
    "salmon-fillet": {
      name: "Salmon fillet",
      price: 400,
      available: true,
      category: "Seafood",
    },
    "olive-oil": {
      name: "Olive oil",
      price: 200,
      available: true,
      category: "Oils",
    },
    lemon: { name: "Lemon", price: 20, available: true, category: "Fruits" },
    "fresh-herbs": {
      name: "Fresh herbs",
      price: 50,
      available: true,
      category: "Herbs",
    },
    tomato: {
      name: "Tomato",
      price: 25,
      available: true,
      category: "Vegetables",
    },
    onion: {
      name: "Onion",
      price: 30,
      available: true,
      category: "Vegetables",
    },
    garlic: {
      name: "Garlic",
      price: 40,
      available: true,
      category: "Vegetables",
    },
  };

  // Mock recipes database
  const recipesDatabase = {
    "Quinoa Buddha Bowl": {
      name: "Quinoa Buddha Bowl",
      description:
        "A nutritious and colorful bowl packed with protein, fiber, and essential nutrients. Perfect for a healthy lunch or dinner.",
      difficulty: "Easy",
      time: "25 min",
      servings: 2,
      calories: 420,
      image: "🥗",
      tags: ["Vegan", "High Protein", "Budget Friendly", "Gluten-Free"],
      ingredients: [
        { name: "Quinoa", quantity: "1 cup", productId: "quinoa-1kg" },
        {
          name: "Sweet potato",
          quantity: "1 medium",
          productId: "sweet-potato-500g",
        },
        { name: "Chickpeas", quantity: "1 can", productId: "chickpeas-can" },
        { name: "Kale", quantity: "2 cups", productId: "kale-bunch" },
        { name: "Avocado", quantity: "1", productId: "avocado" },
        { name: "Olive oil", quantity: "2 tbsp", productId: "olive-oil" },
        { name: "Lemon", quantity: "1/2", productId: "lemon" },
        { name: "Garlic", quantity: "2 cloves", productId: "garlic" },
        { name: "Salt", quantity: "to taste", productId: null },
        { name: "Black pepper", quantity: "to taste", productId: null },
      ],
      instructions: [
        "Rinse quinoa thoroughly and cook according to package instructions. Let it cool.",
        "Preheat oven to 400°F (200°C). Cut sweet potato into cubes and roast for 20-25 minutes until tender.",
        "Drain and rinse chickpeas. Season with olive oil, salt, and pepper.",
        "Massage kale with olive oil and lemon juice to soften it.",
        "Assemble bowls: Start with quinoa base, add roasted sweet potato, chickpeas, and kale.",
        "Top with sliced avocado and drizzle with olive oil and lemon juice.",
        "Season with salt and pepper to taste. Serve immediately.",
      ],
      nutritionInfo: {
        calories: 420,
        protein: "18g",
        carbs: "65g",
        fat: "12g",
        fiber: "15g",
        sugar: "8g",
      },
      tips: [
        "Make extra quinoa for meal prep - it keeps well in the fridge for 3-4 days.",
        "You can substitute any vegetables you have on hand.",
        "For extra protein, add grilled tofu or tempeh.",
        "This recipe is easily customizable based on your dietary preferences.",
      ],
    },
    "Mediterranean Salmon": {
      name: "Mediterranean Salmon",
      description:
        "A healthy and flavorful salmon dish with Mediterranean herbs and vegetables. Rich in omega-3 fatty acids and protein.",
      difficulty: "Medium",
      time: "35 min",
      servings: 2,
      calories: 580,
      image: "🐟",
      tags: ["High Protein", "Omega-3", "Premium", "Low-Carb"],
      ingredients: [
        {
          name: "Salmon fillet",
          quantity: "2 pieces",
          productId: "salmon-fillet",
        },
        { name: "Olive oil", quantity: "3 tbsp", productId: "olive-oil" },
        { name: "Lemon", quantity: "1", productId: "lemon" },
        { name: "Fresh herbs", quantity: "1 bunch", productId: "fresh-herbs" },
        { name: "Quinoa", quantity: "1 cup", productId: "quinoa-1kg" },
        { name: "Cherry tomatoes", quantity: "1 cup", productId: "tomato" },
        { name: "Cucumber", quantity: "1/2", productId: null },
        { name: "Red onion", quantity: "1/4", productId: "onion" },
        { name: "Garlic", quantity: "3 cloves", productId: "garlic" },
        { name: "Salt", quantity: "to taste", productId: null },
        { name: "Black pepper", quantity: "to taste", productId: null },
      ],
      instructions: [
        "Preheat oven to 400°F (200°C). Line a baking sheet with parchment paper.",
        "Season salmon fillets with salt, pepper, and minced garlic. Drizzle with olive oil and lemon juice.",
        "Top with fresh herbs and bake for 12-15 minutes until salmon is flaky and cooked through.",
        "While salmon cooks, prepare quinoa according to package instructions.",
        "Make Mediterranean salad: Combine diced cucumber, cherry tomatoes, red onion, and herbs.",
        "Dress salad with olive oil, lemon juice, salt, and pepper.",
        "Serve salmon over quinoa with Mediterranean salad on the side.",
      ],
      nutritionInfo: {
        calories: 580,
        protein: "45g",
        carbs: "35g",
        fat: "28g",
        fiber: "8g",
        sugar: "6g",
      },
      tips: [
        "Don't overcook salmon - it should be slightly pink in the center.",
        "You can use any fresh herbs you prefer: dill, parsley, or basil work well.",
        "For extra flavor, marinate salmon for 30 minutes before cooking.",
        "This dish pairs perfectly with a crisp white wine.",
      ],
    },
  };

  useEffect(() => {
    loadRecipe();
  }, [id]);

  const loadRecipe = async () => {
    try {
      setLoading(true);
      // Simulate API call
      const recipeName = decodeURIComponent(id);
      const recipeData = recipesDatabase[recipeName];

      if (!recipeData) {
        toast({
          title: "Recipe Not Found",
          description: "The requested recipe could not be found.",
          variant: "destructive",
        });
        navigate("/recipes");
        return;
      }

      // Update ingredients with cart status
      const updatedIngredients = recipeData.ingredients.map((ingredient) => {
        const cartItem = cart.items.find(
          (item) => item.productId === ingredient.productId
        );
        const isAvailable = ingredient.productId
          ? productsDatabase[ingredient.productId]?.available
          : false;

        return {
          ...ingredient,
          inCart: !!cartItem,
          cartQuantity: cartItem ? cartItem.quantity : 0,
          available: isAvailable,
        };
      });

      setRecipe({ ...recipeData, ingredients: updatedIngredients });
    } catch (error) {
      console.error("Error loading recipe:", error);
      toast({
        title: "Error",
        description: "Failed to load recipe. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleAddIngredientToCart = async (ingredient) => {
    if (!ingredient.productId) {
      toast({
        title: "Ingredient Not Available",
        description: `${ingredient.name} is not available for purchase in our store.`,
        variant: "destructive",
      });
      return;
    }

    if (!ingredient.available) {
      toast({
        title: "Product Unavailable",
        description: `${ingredient.name} is currently not available in our store.`,
        variant: "destructive",
      });
      return;
    }

    try {
      await addToCart(ingredient.productId, 1);

      // Update local state
      setRecipe((prev) => ({
        ...prev,
        ingredients: prev.ingredients.map((item) =>
          item.name === ingredient.name
            ? { ...item, inCart: true, cartQuantity: 1 }
            : item
        ),
      }));

      toast({
        title: "Added to Cart",
        description: `${ingredient.name} has been added to your cart!`,
        variant: "default",
      });
    } catch (error) {
      console.error("Error adding ingredient to cart:", error);
      toast({
        title: "Error",
        description: "Failed to add ingredient to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleRemoveIngredientFromCart = async (ingredient) => {
    try {
      await removeFromCart(ingredient.productId);

      // Update local state
      setRecipe((prev) => ({
        ...prev,
        ingredients: prev.ingredients.map((item) =>
          item.name === ingredient.name
            ? { ...item, inCart: false, cartQuantity: 0 }
            : item
        ),
      }));

      toast({
        title: "Removed from Cart",
        description: `${ingredient.name} has been removed from your cart!`,
        variant: "default",
      });
    } catch (error) {
      console.error("Error removing ingredient from cart:", error);
      toast({
        title: "Error",
        description: "Failed to remove ingredient from cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleUpdateIngredientQuantity = async (ingredient, newQuantity) => {
    if (newQuantity <= 0) {
      await handleRemoveIngredientFromCart(ingredient);
      return;
    }

    try {
      await updateQuantity(ingredient.productId, newQuantity);

      // Update local state
      setRecipe((prev) => ({
        ...prev,
        ingredients: prev.ingredients.map((item) =>
          item.name === ingredient.name
            ? { ...item, cartQuantity: newQuantity }
            : item
        ),
      }));

      toast({
        title: "Quantity Updated",
        description: `${ingredient.name} quantity updated to ${newQuantity}!`,
        variant: "default",
      });
    } catch (error) {
      console.error("Error updating ingredient quantity:", error);
      toast({
        title: "Error",
        description: "Failed to update quantity. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleOrderMissingIngredients = async () => {
    try {
      const missingIngredients = recipe.ingredients.filter(
        (ingredient) =>
          !ingredient.inCart && ingredient.available && ingredient.productId
      );

      if (missingIngredients.length === 0) {
        toast({
          title: "All Ingredients Available",
          description:
            "All ingredients for this recipe are already in your cart!",
          variant: "default",
        });
        return;
      }

      // Add all missing ingredients to cart
      for (const ingredient of missingIngredients) {
        await addToCart(ingredient.productId, 1);
      }

      // Update local state
      setRecipe((prev) => ({
        ...prev,
        ingredients: prev.ingredients.map((item) => ({
          ...item,
          inCart: true,
          cartQuantity: item.cartQuantity || 1,
        })),
      }));

      toast({
        title: "Ingredients Added",
        description: `${missingIngredients.length} ingredients have been added to your cart!`,
        variant: "default",
      });

      // Navigate to cart
      navigate("/cart");
    } catch (error) {
      console.error("Error adding ingredients to cart:", error);
      toast({
        title: "Error",
        description:
          "Failed to add some ingredients to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const renderIngredientStatus = (ingredient) => {
    if (!ingredient.productId) {
      return (
        <Badge variant="secondary" className="text-xs">
          Pantry Item
        </Badge>
      );
    }

    if (!ingredient.available) {
      return (
        <Badge variant="destructive" className="text-xs">
          Not Available
        </Badge>
      );
    }

    if (ingredient.inCart) {
      return (
        <div className="flex items-center space-x-2">
          <div className="flex items-center space-x-1">
            <button
              onClick={() =>
                handleUpdateIngredientQuantity(
                  ingredient,
                  ingredient.cartQuantity - 1
                )
              }
              className="w-6 h-6 rounded-full bg-red-100 text-red-600 hover:bg-red-200 flex items-center justify-center text-sm font-bold"
            >
              -
            </button>
            <span className="text-sm font-medium text-gray-700 min-w-[20px] text-center">
              {ingredient.cartQuantity}
            </span>
            <button
              onClick={() =>
                handleUpdateIngredientQuantity(
                  ingredient,
                  ingredient.cartQuantity + 1
                )
              }
              className="w-6 h-6 rounded-full bg-green-100 text-green-600 hover:bg-green-200 flex items-center justify-center text-sm font-bold"
            >
              +
            </button>
          </div>
          <Button
            variant="outline"
            size="sm"
            className="text-xs text-red-600 hover:text-red-700"
            onClick={() => handleRemoveIngredientFromCart(ingredient)}
          >
            Remove
          </Button>
        </div>
      );
    }

    return (
      <Button
        variant="outline"
        size="sm"
        className="text-xs"
        onClick={() => handleAddIngredientToCart(ingredient)}
      >
        Add to Cart
      </Button>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading recipe...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!recipe) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <p className="text-gray-600 text-lg">Recipe not found.</p>
            <Button onClick={() => navigate("/recipes")} className="mt-4">
              Back to Recipes
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header with Back Navigation */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => navigate("/recipes")}
              className="text-gray-600 hover:text-green-600 transition-colors"
            >
              <svg
                className="w-6 h-6"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M10 19l-7-7m0 0l7-7m-7 7h18"
                />
              </svg>
            </button>
            <h1 className="text-2xl font-bold text-gray-900">{recipe.name}</h1>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Recipe Hero */}
        <div className="bg-white rounded-2xl shadow-lg overflow-hidden mb-8">
          <div className="bg-gradient-to-r from-primary to-secondary p-8 text-white">
            <div className="flex items-center space-x-4 mb-4">
              <div className="text-6xl">{recipe.image}</div>
              <div>
                <h2 className="text-3xl font-bold font-heading">
                  {recipe.name}
                </h2>
                <p className="text-lg opacity-90">{recipe.description}</p>
              </div>
            </div>
            <div className="flex items-center space-x-6">
              <Badge variant="secondary" className="text-sm">
                ⏱️ {recipe.time}
              </Badge>
              <Badge variant="secondary" className="text-sm">
                👥 {recipe.servings} servings
              </Badge>
              <Badge variant="secondary" className="text-sm">
                🔥 {recipe.calories} cal
              </Badge>
              <Badge variant="secondary" className="text-sm">
                📊 {recipe.difficulty}
              </Badge>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-6">
            {/* Tags */}
            <Card>
              <CardHeader>
                <CardTitle className="text-xl">Recipe Tags</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="flex flex-wrap gap-2">
                  {recipe.tags.map((tag, index) => (
                    <Badge key={index} variant="secondary" className="text-sm">
                      {tag}
                    </Badge>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Ingredients */}
            <Card>
              <CardHeader>
                <CardTitle className="text-xl">Ingredients</CardTitle>
                <p className="text-sm text-muted">
                  Manage your shopping list for this recipe
                </p>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {recipe.ingredients.map((ingredient, idx) => (
                    <div
                      key={idx}
                      className="flex items-center justify-between p-3 rounded-lg bg-gray-50"
                    >
                      <div className="flex items-center space-x-3">
                        <div
                          className={`w-3 h-3 rounded-full ${
                            ingredient.inCart
                              ? "bg-success"
                              : ingredient.available
                              ? "bg-warning"
                              : "bg-gray-400"
                          }`}
                        ></div>
                        <div>
                          <span className="text-sm text-foreground font-medium">
                            {ingredient.name}
                          </span>
                          <div className="text-xs text-muted">
                            {ingredient.quantity}
                            {ingredient.productId && ingredient.available && (
                              <span className="ml-2 text-green-600">
                                • ₹
                                {productsDatabase[ingredient.productId]?.price}
                              </span>
                            )}
                          </div>
                        </div>
                      </div>
                      {renderIngredientStatus(ingredient)}
                    </div>
                  ))}
                </div>

                <div className="mt-6">
                  <Button
                    variant="gradient"
                    className="w-full"
                    onClick={handleOrderMissingIngredients}
                    disabled={recipe.ingredients.every(
                      (ingredient) =>
                        ingredient.inCart ||
                        !ingredient.available ||
                        !ingredient.productId
                    )}
                  >
                    Order Missing Ingredients
                  </Button>
                </div>
              </CardContent>
            </Card>

            {/* Instructions */}
            <Card>
              <CardHeader>
                <CardTitle className="text-xl">Instructions</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {recipe.instructions.map((instruction, index) => (
                    <div key={index} className="flex items-start space-x-3">
                      <div className="w-8 h-8 bg-primary text-white rounded-full flex items-center justify-center text-sm font-bold flex-shrink-0">
                        {index + 1}
                      </div>
                      <p className="text-gray-700">{instruction}</p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Tips */}
            <Card>
              <CardHeader>
                <CardTitle className="text-xl">Chef's Tips</CardTitle>
              </CardHeader>
              <CardContent>
                <ul className="space-y-2">
                  {recipe.tips.map((tip, index) => (
                    <li key={index} className="flex items-start space-x-3">
                      <div className="w-2 h-2 bg-primary rounded-full mt-2 flex-shrink-0"></div>
                      <span className="text-gray-700">{tip}</span>
                    </li>
                  ))}
                </ul>
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Nutrition Information */}
            <Card>
              <CardHeader>
                <CardTitle className="text-xl">Nutrition Information</CardTitle>
                <p className="text-sm text-muted">Per serving</p>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 gap-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-primary">
                      {recipe.nutritionInfo.calories}
                    </div>
                    <div className="text-sm text-gray-600">Calories</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-secondary">
                      {recipe.nutritionInfo.protein}
                    </div>
                    <div className="text-sm text-gray-600">Protein</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-accent">
                      {recipe.nutritionInfo.carbs}
                    </div>
                    <div className="text-sm text-gray-600">Carbs</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-success">
                      {recipe.nutritionInfo.fat}
                    </div>
                    <div className="text-sm text-gray-600">Fat</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-purple-600">
                      {recipe.nutritionInfo.fiber}
                    </div>
                    <div className="text-sm text-gray-600">Fiber</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-orange-600">
                      {recipe.nutritionInfo.sugar}
                    </div>
                    <div className="text-sm text-gray-600">Sugar</div>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Quick Actions */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Quick Actions</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <Button
                  variant="outline"
                  className="w-full"
                  onClick={() => navigate("/cart")}
                >
                  View Cart
                </Button>
                <Button
                  variant="outline"
                  className="w-full"
                  onClick={() => navigate("/recipes")}
                >
                  Browse More Recipes
                </Button>
                <Button
                  variant="outline"
                  className="w-full"
                  onClick={() => navigate("/products")}
                >
                  Shop Products
                </Button>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}
