import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../../contexts/CartContext";
import { useAuth } from "../../contexts/AuthContext";
import { useToast } from "./use-toast";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "./card";
import { Button } from "./button";
import { Badge } from "./badge";

export function RecipeIntegration() {
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { user } = useAuth();
  const { toast } = useToast();

  const [recipeIngredients, setRecipeIngredients] = useState({
    "Quinoa Buddha Bowl": [
      {
        name: "Quinoa",
        quantity: "1 cup",
        inPantry: true,
        productId: "quinoa-1kg",
      },
      {
        name: "Sweet potato",
        quantity: "1 medium",
        inPantry: false,
        productId: "sweet-potato-500g",
      },
      {
        name: "Chickpeas",
        quantity: "1 can",
        inPantry: false,
        productId: "chickpeas-can",
      },
      {
        name: "Kale",
        quantity: "2 cups",
        inPantry: false,
        productId: "kale-bunch",
      },
      { name: "Avocado", quantity: "1", inPantry: false, productId: "avocado" },
    ],
    "Mediterranean Salmon": [
      {
        name: "Salmon fillet",
        quantity: "2 pieces",
        inPantry: false,
        productId: "salmon-fillet",
      },
      {
        name: "Olive oil",
        quantity: "2 tbsp",
        inPantry: true,
        productId: "olive-oil",
      },
      { name: "Lemon", quantity: "1", inPantry: false, productId: "lemon" },
      {
        name: "Herbs",
        quantity: "1 bunch",
        inPantry: false,
        productId: "fresh-herbs",
      },
      {
        name: "Quinoa",
        quantity: "1 cup",
        inPantry: true,
        productId: "quinoa-1kg",
      },
    ],
  });

  const handleAddIngredientToCart = async (recipeName, ingredient) => {
    try {
      // Add ingredient to cart
      await addToCart(ingredient.productId, 1);

      // Update local state to show ingredient as added
      setRecipeIngredients((prev) => ({
        ...prev,
        [recipeName]: prev[recipeName].map((item) =>
          item.name === ingredient.name ? { ...item, inPantry: true } : item
        ),
      }));

      toast({
        title: "Added to Cart",
        description: `${ingredient.name} has been added to your cart!`,
        variant: "default",
      });
    } catch (error) {
      console.error("Error adding ingredient to cart:", error);
      toast({
        title: "Error",
        description: "Failed to add ingredient to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleOrderMissingIngredients = async (recipeName) => {
    try {
      const missingIngredients = recipeIngredients[recipeName].filter(
        (ingredient) => !ingredient.inPantry
      );

      // Add all missing ingredients to cart
      for (const ingredient of missingIngredients) {
        await addToCart(ingredient.productId, 1);
      }

      // Update local state
      setRecipeIngredients((prev) => ({
        ...prev,
        [recipeName]: prev[recipeName].map((item) => ({
          ...item,
          inPantry: true,
        })),
      }));

      toast({
        title: "Ingredients Added",
        description: `${missingIngredients.length} ingredients have been added to your cart!`,
        variant: "default",
      });

      // Navigate to cart
      navigate("/cart");
    } catch (error) {
      console.error("Error adding ingredients to cart:", error);
      toast({
        title: "Error",
        description:
          "Failed to add some ingredients to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const sampleRecipes = [
    {
      name: "Quinoa Buddha Bowl",
      difficulty: "Easy",
      time: "25 min",
      servings: 2,
      calories: 420,
      ingredients: recipeIngredients["Quinoa Buddha Bowl"],
      image: "ü•ó",
      tags: ["Vegan", "High Protein", "Budget Friendly"],
    },
    {
      name: "Mediterranean Salmon",
      difficulty: "Medium",
      time: "35 min",
      servings: 2,
      calories: 580,
      ingredients: recipeIngredients["Mediterranean Salmon"],
      image: "üêü",
      tags: ["High Protein", "Omega-3", "Premium"],
    },
  ];

  return (
    <section className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <Badge variant="info" className="mb-4 text-sm">
            üë®‚Äçüç≥ Smart Cooking Mode
          </Badge>
          <h2 className="text-3xl font-bold text-foreground sm:text-4xl lg:text-5xl mb-6 font-heading">
            Recipe Integration +
            <span className="block text-transparent bg-clip-text bg-gradient-to-r from-primary to-secondary">
              Smart Ingredient Ordering
            </span>
          </h2>
          <p className="text-xl text-muted max-w-3xl mx-auto font-body">
            Each diet plan generates weekly recipe suggestions with step-by-step
            instructions. Order missing ingredients with one click and sync with
            your pantry.
          </p>
        </div>

        {/* Recipe Cards */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-16">
          {sampleRecipes.map((recipe, index) => (
            <Card
              key={index}
              className="group hover:shadow-xl transition-all duration-300 overflow-hidden"
            >
              <div className="h-2 bg-gradient-to-r from-primary to-secondary"></div>

              <CardHeader className="pb-4">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <CardTitle className="text-2xl font-bold text-foreground font-heading">
                      {recipe.name}
                    </CardTitle>
                    <CardDescription className="text-muted mt-2 font-body">
                      A delicious and nutritious meal perfect for your diet plan
                    </CardDescription>
                  </div>
                  <div className="text-4xl ml-4">{recipe.image}</div>
                </div>

                {/* Recipe Meta */}
                <div className="flex items-center space-x-4 mt-4">
                  <Badge variant="outline" className="text-xs">
                    ‚è±Ô∏è {recipe.time}
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    üë• {recipe.servings} servings
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    üî• {recipe.calories} cal
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    üìä {recipe.difficulty}
                  </Badge>
                </div>

                {/* Tags */}
                <div className="flex flex-wrap gap-2 mt-3">
                  {recipe.tags.map((tag, idx) => (
                    <Badge key={idx} variant="secondary" className="text-xs">
                      {tag}
                    </Badge>
                  ))}
                </div>
              </CardHeader>

              <CardContent className="space-y-6">
                {/* Ingredients List */}
                <div>
                  <h4 className="font-semibold text-foreground mb-3 font-heading">
                    Ingredients
                  </h4>
                  <div className="space-y-2">
                    {recipe.ingredients.map((ingredient, idx) => (
                      <div
                        key={idx}
                        className="flex items-center justify-between p-2 rounded-lg bg-gray-50"
                      >
                        <div className="flex items-center space-x-3">
                          <div
                            className={`w-3 h-3 rounded-full ${
                              ingredient.inPantry ? "bg-success" : "bg-warning"
                            }`}
                          ></div>
                          <span className="text-sm text-foreground font-medium">
                            {ingredient.name}
                          </span>
                          <span className="text-xs text-muted">
                            {ingredient.quantity}
                          </span>
                        </div>
                        {!ingredient.inPantry && (
                          <Button
                            variant="outline"
                            size="sm"
                            className="text-xs"
                            onClick={() =>
                              handleAddIngredientToCart(recipe.name, ingredient)
                            }
                          >
                            Add to Cart
                          </Button>
                        )}
                        {ingredient.inPantry && (
                          <Badge variant="success" className="text-xs">
                            ‚úì Added
                          </Badge>
                        )}
                      </div>
                    ))}
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-3">
                  <Button
                    variant="outline"
                    className="flex-1"
                    size="sm"
                    onClick={() =>
                      navigate(`/recipes/${encodeURIComponent(recipe.name)}`)
                    }
                  >
                    View Full Recipe
                  </Button>
                  <Button
                    variant="gradient"
                    className="flex-1"
                    size="sm"
                    onClick={() => handleOrderMissingIngredients(recipe.name)}
                    disabled={recipe.ingredients.every(
                      (ingredient) => ingredient.inPantry
                    )}
                  >
                    Order Missing Ingredients
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Smart Features Showcase */}
        <div className="bg-gradient-to-r from-primary/5 to-secondary/5 rounded-3xl p-12">
          <div className="text-center mb-12">
            <h3 className="text-2xl font-bold text-foreground mb-4 font-heading">
              üöÄ Smart Cooking Features
            </h3>
            <p className="text-muted max-w-2xl mx-auto font-body">
              Our smart cooking assistant makes meal preparation effortless and
              cost-effective
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center">
              <div className="w-20 h-20 bg-primary/10 rounded-full flex items-center mx-auto mb-4">
                <span className="text-3xl">üì±</span>
              </div>
              <h4 className="font-semibold text-foreground mb-3 font-heading">
                One-Click Ordering
              </h4>
              <p className="text-sm text-muted font-body">
                Order missing ingredients for any recipe with a single tap. We
                automatically add only what you need.
              </p>
            </div>

            <div className="text-center">
              <div className="w-20 h-20 bg-secondary/10 rounded-full flex items-center mx-auto mb-4">
                <span className="text-3xl">üè†</span>
              </div>
              <h4 className="font-semibold text-foreground mb-3 font-heading">
                Pantry Sync
              </h4>
              <p className="text-sm text-muted font-body">
                Upload photos of your pantry or input manually. We suggest
                recipes using what you already have.
              </p>
            </div>

            <div className="text-center">
              <div className="w-20 h-20 bg-accent/10 rounded-full flex items-center mx-auto mb-4">
                <span className="text-3xl">üéØ</span>
              </div>
              <h4 className="font-semibold text-foreground mb-3 font-heading">
                Diet Optimization
              </h4>
              <p className="text-sm text-muted font-body">
                Recipes automatically adjust to your dietary restrictions,
                allergies, and health goals.
              </p>
            </div>
          </div>

          {/* CTA Section */}
          <div className="text-center mt-12">
            <div className="bg-white rounded-2xl p-8 shadow-lg border border-gray-200 max-w-2xl mx-auto">
              <h4 className="text-xl font-bold text-foreground mb-4 font-heading">
                Ready to Start Cooking Smart?
              </h4>
              <p className="text-muted mb-6 font-body">
                Get personalized recipes, smart ingredient ordering, and save
                money on groceries
              </p>
              <div className="flex justify-center">
                <Button
                  variant="gradient"
                  size="lg"
                  onClick={() => navigate("/recipes")}
                >
                  View Free Recipes
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
