import React, { createContext, useContext, useState, useEffect } from "react";
import { toast } from "../components/ui/use-toast";
import { getProductById } from "../data/mockDatabase";

const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error("useCart must be used within a CartProvider");
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState({ items: [], total: 0, count: 0 });
  const [loading, setLoading] = useState(false);

  // Load cart from localStorage on init
  useEffect(() => {
    const savedCart = localStorage.getItem("nutriCart");
    if (savedCart) {
      try {
        setCart(JSON.parse(savedCart));
      } catch (error) {
        console.error("Error loading cart from localStorage:", error);
        setCart({ items: [], total: 0, count: 0 });
      }
    }
  }, []);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("nutriCart", JSON.stringify(cart));
  }, [cart]);

  // Calculate cart totals
  const calculateTotals = (items) => {
    const total = items.reduce(
      (sum, item) => sum + item.price * item.quantity,
      0
    );
    const count = items.reduce((sum, item) => sum + item.quantity, 0);
    return { total, count };
  };

  // Add item to cart
  const addToCart = (productId, quantity = 1) => {
    const product = getProductById(productId);
    if (!product) {
      toast({
        title: "Error",
        description: "Product not found",
        variant: "destructive",
      });
      return;
    }

    if (!product.inStock) {
      toast({
        title: "Out of Stock",
        description: `${product.name} is currently out of stock`,
        variant: "destructive",
      });
      return;
    }

    setCart((prevCart) => {
      const existingItemIndex = prevCart.items.findIndex(
        (item) => item.id === productId
      );
      let newItems;

      if (existingItemIndex > -1) {
        // Update existing item
        newItems = [...prevCart.items];
        newItems[existingItemIndex] = {
          ...newItems[existingItemIndex],
          quantity: newItems[existingItemIndex].quantity + quantity,
        };
      } else {
        // Add new item
        newItems = [
          ...prevCart.items,
          {
            id: product.id,
            name: product.name,
            price: product.price,
            image: product.image,
            unit: product.unit,
            quantity: quantity,
            category: product.category,
          },
        ];
      }

      const { total, count } = calculateTotals(newItems);

      toast({
        title: "Added to Cart",
        description: `${product.name} has been added to your cart`,
        variant: "default",
      });

      return { items: newItems, total, count };
    });
  };

  // Update item quantity
  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
      return;
    }

    setCart((prevCart) => {
      const newItems = prevCart.items.map((item) =>
        item.id === productId ? { ...item, quantity: newQuantity } : item
      );

      const { total, count } = calculateTotals(newItems);
      return { items: newItems, total, count };
    });
  };

  // Remove item from cart
  const removeFromCart = (productId) => {
    setCart((prevCart) => {
      const newItems = prevCart.items.filter((item) => item.id !== productId);
      const { total, count } = calculateTotals(newItems);

      toast({
        title: "Removed from Cart",
        description: "Item has been removed from your cart",
        variant: "default",
      });

      return { items: newItems, total, count };
    });
  };

  // Clear entire cart
  const clearCart = () => {
    setCart({ items: [], total: 0, count: 0 });
    toast({
      title: "Cart Cleared",
      description: "All items have been removed from your cart",
      variant: "default",
    });
  };

  // Get item quantity by product ID
  const getItemQuantity = (productId) => {
    const item = cart.items.find((item) => item.id === productId);
    return item ? item.quantity : 0;
  };

  // Check if item is in cart
  const isInCart = (productId) => {
    return cart.items.some((item) => item.id === productId);
  };

  const value = {
    cart,
    loading,
    addToCart,
    updateQuantity,
    removeFromCart,
    clearCart,
    getItemQuantity,
    isInCart,
  };

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
};
