import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useCart } from "../contexts/CartContext";
import { useAuth } from "../contexts/AuthContext";
import { Button } from "../components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { toast } from "../components/ui/use-toast";

export default function RecipePage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { isAuthenticated } = useAuth();
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [missingIngredients, setMissingIngredients] = useState([]);

  useEffect(() => {
    fetchRecipe();
  }, [id]);

  const fetchRecipe = async () => {
    try {
      setLoading(true);
      // For now, using mock data - replace with API call
      const mockRecipe = {
        _id: id,
        name: "Keto Avocado Bowl",
        description: "A delicious and nutritious keto-friendly breakfast bowl",
        difficulty: "Easy",
        time: "15 minutes",
        servings: 2,
        calories: 450,
        image: "🥑",
        tags: ["keto", "breakfast", "vegetarian"],
        ingredients: [
          {
            name: "Avocado",
            quantity: "2 medium",
            inPantry: false,
            productId: "avocado_product_id",
          },
          {
            name: "Eggs",
            quantity: "4 large",
            inPantry: false,
            productId: "eggs_product_id",
          },
          {
            name: "Cherry Tomatoes",
            quantity: "1 cup",
            inPantry: false,
            productId: "tomatoes_product_id",
          },
          {
            name: "Olive Oil",
            quantity: "2 tbsp",
            inPantry: true,
            productId: "olive_oil_product_id",
          },
        ],
        instructions: [
          "Cut avocados in half and remove pits",
          "Fry eggs to your preference",
          "Arrange avocado halves on a plate",
          "Top with fried eggs and cherry tomatoes",
          "Drizzle with olive oil and season with salt and pepper",
        ],
        nutrition: {
          calories: 450,
          protein: "18g",
          fat: "38g",
          carbs: "12g",
          fiber: "8g",
        },
        dietCompatible: ["keto", "low-carb", "vegetarian"],
        author: "Chef Sarah",
        rating: 4.8,
        reviews: 127,
      };

      setRecipe(mockRecipe);

      // Filter missing ingredients (not in pantry)
      const missing = mockRecipe.ingredients.filter((ing) => !ing.inPantry);
      setMissingIngredients(missing);
    } catch (error) {
      console.error("Error fetching recipe:", error);
      toast({
        title: "Error",
        description: "Failed to load recipe. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleAddIngredientToCart = (ingredient) => {
    // For now, using a mock product ID - in real app, this would be the actual product ID
    const mockProductId =
      ingredient.productId ||
      `product_${ingredient.name.toLowerCase().replace(/\s+/g, "_")}`;

    addToCart(mockProductId, 1);
    toast({
      title: "Added to Cart",
      description: `${ingredient.name} has been added to your cart!`,
      variant: "default",
    });
  };

  const handleAddAllMissingIngredients = () => {
    missingIngredients.forEach((ingredient) => {
      const mockProductId =
        ingredient.productId ||
        `product_${ingredient.name.toLowerCase().replace(/\s+/g, "_")}`;
      addToCart(mockProductId, 1);
    });

    toast({
      title: "Ingredients Added",
      description: "All missing ingredients have been added to your cart!",
      variant: "default",
    });

    // Redirect to cart
    navigate("/cart");
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading recipe...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!recipe) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h1 className="text-2xl font-bold text-gray-900">
              Recipe not found
            </h1>
            <Button onClick={() => navigate("/")} className="mt-4">
              Go Home
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="bg-white shadow-sm border-b mb-8 rounded-lg">
          <div className="px-6 py-4">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate(-1)}
                className="text-gray-600 hover:text-green-600 transition-colors"
              >
                <svg
                  className="w-6 h-6"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M10 19l-7-7m0 0l7-7m-7 7h18"
                  />
                </svg>
              </button>
              <h1 className="text-2xl font-bold text-gray-900">
                {recipe.name}
              </h1>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Recipe Details */}
          <div className="lg:col-span-2 space-y-6">
            {/* Recipe Header */}
            <Card>
              <CardContent className="p-6">
                <div className="flex items-start space-x-6">
                  <div className="text-6xl">{recipe.image}</div>
                  <div className="flex-1">
                    <h2 className="text-3xl font-bold text-gray-900 mb-2">
                      {recipe.name}
                    </h2>
                    <p className="text-gray-600 mb-4">{recipe.description}</p>

                    <div className="flex flex-wrap gap-2 mb-4">
                      {recipe.tags.map((tag, index) => (
                        <Badge key={index} variant="secondary">
                          {tag}
                        </Badge>
                      ))}
                    </div>

                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                      <div>
                        <span className="text-gray-500">Difficulty:</span>
                        <p className="font-medium">{recipe.difficulty}</p>
                      </div>
                      <div>
                        <span className="text-gray-500">Time:</span>
                        <p className="font-medium">{recipe.time}</p>
                      </div>
                      <div>
                        <span className="text-gray-500">Servings:</span>
                        <p className="font-medium">{recipe.servings}</p>
                      </div>
                      <div>
                        <span className="text-gray-500">Calories:</span>
                        <p className="font-medium">{recipe.calories}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Ingredients */}
            <Card>
              <CardHeader>
                <CardTitle>Ingredients</CardTitle>
                <CardDescription>
                  {recipe.ingredients.length} ingredients needed
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {recipe.ingredients.map((ingredient, index) => (
                    <div
                      key={index}
                      className={`flex items-center justify-between p-3 rounded-lg border ${
                        ingredient.inPantry
                          ? "bg-green-50 border-green-200"
                          : "bg-red-50 border-red-200"
                      }`}
                    >
                      <div className="flex items-center space-x-3">
                        <span className="text-lg">
                          {ingredient.inPantry ? "✅" : "❌"}
                        </span>
                        <div>
                          <p className="font-medium">{ingredient.name}</p>
                          <p className="text-sm text-gray-600">
                            {ingredient.quantity}
                          </p>
                        </div>
                      </div>

                      {!ingredient.inPantry && (
                        <Button
                          size="sm"
                          onClick={() => handleAddIngredientToCart(ingredient)}
                          className="bg-green-600 hover:bg-green-700"
                        >
                          Add to Cart
                        </Button>
                      )}
                    </div>
                  ))}
                </div>

                {missingIngredients.length > 0 && (
                  <div className="mt-6 p-4 bg-blue-50 rounded-lg border border-blue-200">
                    <h4 className="font-medium text-blue-900 mb-2">
                      Missing {missingIngredients.length} ingredients
                    </h4>
                    <p className="text-sm text-blue-700 mb-3">
                      Add all missing ingredients to your cart with one click!
                    </p>
                    <Button
                      onClick={handleAddAllMissingIngredients}
                      className="bg-blue-600 hover:bg-blue-700"
                    >
                      Add All Missing Ingredients to Cart
                    </Button>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Instructions */}
            <Card>
              <CardHeader>
                <CardTitle>Instructions</CardTitle>
                <CardDescription>
                  Step-by-step cooking instructions
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {recipe.instructions.map((step, index) => (
                    <div key={index} className="flex space-x-4">
                      <div className="flex-shrink-0 w-8 h-8 bg-green-100 rounded-full flex items-center justify-center text-green-700 font-medium">
                        {index + 1}
                      </div>
                      <p className="text-gray-700">{step}</p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Nutrition Info */}
            <Card>
              <CardHeader>
                <CardTitle>Nutrition Information</CardTitle>
                <CardDescription>
                  Per serving nutritional values
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  <div className="text-center p-3 bg-gray-50 rounded-lg">
                    <p className="text-2xl font-bold text-green-600">
                      {recipe.nutrition.calories}
                    </p>
                    <p className="text-sm text-gray-600">Calories</p>
                  </div>
                  <div className="text-center p-3 bg-gray-50 rounded-lg">
                    <p className="text-2xl font-bold text-blue-600">
                      {recipe.nutrition.protein}
                    </p>
                    <p className="text-sm text-gray-600">Protein</p>
                  </div>
                  <div className="text-center p-3 bg-gray-50 rounded-lg">
                    <p className="text-2xl font-bold text-yellow-600">
                      {recipe.nutrition.carbs}
                    </p>
                    <p className="text-sm text-gray-600">Carbs</p>
                  </div>
                  <div className="text-center p-3 bg-gray-50 rounded-lg">
                    <p className="text-2xl font-bold text-red-600">
                      {recipe.nutrition.fat}
                    </p>
                    <p className="text-sm text-gray-600">Fat</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="lg:col-span-1">
            <Card className="sticky top-24">
              <CardHeader>
                <CardTitle>Recipe Info</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="text-center">
                  <div className="text-4xl mb-2">{recipe.image}</div>
                  <div className="flex items-center justify-center space-x-1 mb-2">
                    {[...Array(5)].map((_, i) => (
                      <span
                        key={i}
                        className={`text-lg ${
                          i < Math.floor(recipe.rating)
                            ? "text-yellow-400"
                            : "text-gray-300"
                        }`}
                      >
                        ★
                      </span>
                    ))}
                  </div>
                  <p className="text-sm text-gray-600">
                    {recipe.rating} ({recipe.reviews} reviews)
                  </p>
                </div>

                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Author:</span>
                    <span className="font-medium">{recipe.author}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Diet Compatible:</span>
                    <span className="font-medium">
                      {recipe.dietCompatible.join(", ")}
                    </span>
                  </div>
                </div>

                <Button
                  onClick={() => navigate("/recipes")}
                  className="w-full"
                  variant="outline"
                >
                  View More Recipes
                </Button>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}
