import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from "../components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { Input } from "../components/ui/input";
import { Search, Clock, Users, Star, Filter } from "lucide-react";

export default function RecipesPage() {
  const navigate = useNavigate();
  const [recipes, setRecipes] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedDifficulty, setSelectedDifficulty] = useState("all");
  const [selectedDiet, setSelectedDiet] = useState("all");

  useEffect(() => {
    fetchRecipes();
  }, []);

  useEffect(() => {
    filterRecipes();
  }, [searchTerm, selectedDifficulty, selectedDiet, recipes]);

  const fetchRecipes = async () => {
    try {
      setLoading(true);
      // Mock data - replace with API call
      const mockRecipes = [
        {
          _id: "1",
          name: "Keto Avocado Bowl",
          description:
            "A delicious and nutritious keto-friendly breakfast bowl",
          difficulty: "Easy",
          time: "15 minutes",
          servings: 2,
          calories: 450,
          image: "ü•ë",
          tags: ["keto", "breakfast", "vegetarian"],
          dietCompatible: ["keto", "low-carb", "vegetarian"],
          rating: 4.8,
          reviews: 127,
        },
        {
          _id: "2",
          name: "Mediterranean Quinoa Salad",
          description:
            "Fresh and healthy quinoa salad with Mediterranean flavors",
          difficulty: "Easy",
          time: "20 minutes",
          servings: 4,
          calories: 320,
          image: "ü•ó",
          tags: ["mediterranean", "vegan", "gluten-free"],
          dietCompatible: ["mediterranean", "vegetarian", "gluten-free"],
          rating: 4.6,
          reviews: 89,
        },
        {
          _id: "3",
          name: "Protein Power Smoothie",
          description:
            "High-protein smoothie perfect for post-workout recovery",
          difficulty: "Easy",
          time: "5 minutes",
          servings: 1,
          calories: 280,
          image: "ü•§",
          tags: ["protein", "smoothie", "post-workout"],
          dietCompatible: ["high-protein", "vegetarian"],
          rating: 4.7,
          reviews: 156,
        },
        {
          _id: "4",
          name: "Low-Carb Cauliflower Pizza",
          description: "Delicious pizza crust made from cauliflower",
          difficulty: "Medium",
          time: "45 minutes",
          servings: 4,
          calories: 380,
          image: "üçï",
          tags: ["low-carb", "pizza", "cauliflower"],
          dietCompatible: ["keto", "low-carb", "gluten-free"],
          rating: 4.5,
          reviews: 203,
        },
      ];

      setRecipes(mockRecipes);
      setFilteredRecipes(mockRecipes);
    } catch (error) {
      console.error("Error fetching recipes:", error);
    } finally {
      setLoading(false);
    }
  };

  const filterRecipes = () => {
    let filtered = recipes;

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(
        (recipe) =>
          recipe.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          recipe.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
          recipe.tags.some((tag) =>
            tag.toLowerCase().includes(searchTerm.toLowerCase())
          )
      );
    }

    // Difficulty filter
    if (selectedDifficulty !== "all") {
      filtered = filtered.filter(
        (recipe) => recipe.difficulty === selectedDifficulty
      );
    }

    // Diet filter
    if (selectedDiet !== "all") {
      filtered = filtered.filter((recipe) =>
        recipe.dietCompatible.includes(selectedDiet)
      );
    }

    setFilteredRecipes(filtered);
  };

  const handleViewRecipe = (recipeId) => {
    navigate(`/recipes/${recipeId}`);
  };

  const difficulties = ["all", "Easy", "Medium", "Hard"];
  const diets = [
    "all",
    "keto",
    "mediterranean",
    "vegetarian",
    "vegan",
    "low-carb",
    "high-protein",
    "gluten-free",
  ];

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading recipes...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="bg-white shadow-sm border-b mb-8 rounded-lg">
          <div className="px-6 py-4">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate("/")}
                className="text-gray-600 hover:text-green-600 transition-colors"
              >
                <svg
                  className="w-6 h-6"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M10 19l-7-7m0 0l7-7m-7 7h18"
                  />
                </svg>
              </button>
              <h1 className="text-2xl font-bold text-gray-900">
                Healthy Recipes
              </h1>
            </div>
          </div>
        </div>

        {/* Search and Filters */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            {/* Search */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                placeholder="Search recipes..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>

            {/* Difficulty Filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Difficulty
              </label>
              <select
                value={selectedDifficulty}
                onChange={(e) => setSelectedDifficulty(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              >
                {difficulties.map((difficulty) => (
                  <option key={difficulty} value={difficulty}>
                    {difficulty === "all" ? "All Difficulties" : difficulty}
                  </option>
                ))}
              </select>
            </div>

            {/* Diet Filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Diet Type
              </label>
              <select
                value={selectedDiet}
                onChange={(e) => setSelectedDiet(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              >
                {diets.map((diet) => (
                  <option key={diet} value={diet}>
                    {diet === "all"
                      ? "All Diets"
                      : diet.charAt(0).toUpperCase() + diet.slice(1)}
                  </option>
                ))}
              </select>
            </div>

            {/* Clear Filters */}
            <div className="flex items-end">
              <Button
                onClick={() => {
                  setSearchTerm("");
                  setSelectedDifficulty("all");
                  setSelectedDiet("all");
                }}
                variant="outline"
                className="w-full"
              >
                Clear Filters
              </Button>
            </div>
          </div>
        </div>

        {/* Results Count */}
        <div className="mb-6">
          <p className="text-gray-600">
            Showing {filteredRecipes.length} of {recipes.length} recipes
          </p>
        </div>

        {/* Recipes Grid */}
        {filteredRecipes.length === 0 ? (
          <div className="text-center py-16">
            <div className="text-6xl mb-6">üçΩÔ∏è</div>
            <h2 className="text-2xl font-bold text-gray-900 mb-4">
              No recipes found
            </h2>
            <p className="text-gray-600 mb-8">
              Try adjusting your search criteria or filters.
            </p>
            <Button
              onClick={() => {
                setSearchTerm("");
                setSelectedDifficulty("all");
                setSelectedDiet("all");
              }}
              className="bg-green-600 hover:bg-green-700"
            >
              Clear All Filters
            </Button>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredRecipes.map((recipe) => (
              <Card
                key={recipe._id}
                className="overflow-hidden hover:shadow-lg transition-shadow"
              >
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <div className="text-4xl">{recipe.image}</div>
                    <Badge variant="secondary">{recipe.difficulty}</Badge>
                  </div>
                  <CardTitle className="text-lg">{recipe.name}</CardTitle>
                  <CardDescription className="line-clamp-2">
                    {recipe.description}
                  </CardDescription>
                </CardHeader>

                <CardContent className="pt-0">
                  {/* Recipe Stats */}
                  <div className="flex items-center space-x-4 text-sm text-gray-600 mb-4">
                    <div className="flex items-center space-x-1">
                      <Clock className="w-4 h-4" />
                      <span>{recipe.time}</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <Users className="w-4 h-4" />
                      <span>{recipe.servings} servings</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <Star className="w-4 h-4 text-yellow-400 fill-current" />
                      <span>{recipe.rating}</span>
                    </div>
                  </div>

                  {/* Calories */}
                  <div className="mb-4">
                    <span className="text-sm text-gray-500">
                      Calories per serving:
                    </span>
                    <p className="font-semibold text-green-600">
                      {recipe.calories} cal
                    </p>
                  </div>

                  {/* Tags */}
                  <div className="flex flex-wrap gap-2 mb-4">
                    {recipe.tags.slice(0, 3).map((tag, index) => (
                      <Badge key={index} variant="outline" className="text-xs">
                        {tag}
                      </Badge>
                    ))}
                    {recipe.tags.length > 3 && (
                      <Badge variant="outline" className="text-xs">
                        +{recipe.tags.length - 3} more
                      </Badge>
                    )}
                  </div>

                  {/* View Recipe Button */}
                  <Button
                    onClick={() => handleViewRecipe(recipe._id)}
                    className="w-full bg-green-600 hover:bg-green-700"
                  >
                    View Recipe
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
