import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../contexts/CartContext";
import { useToast } from "../components/ui/use-toast";
import { Button } from "../components/ui/button";
import { Card } from "../components/ui/card";
import { Input } from "../components/ui/input";
import {
  Search,
  Filter,
  Star,
  ShoppingCart,
  Heart,
  Eye,
  TrendingUp,
  Award,
  Truck,
  Shield,
  Sparkles,
  Zap,
  Target,
  Clock,
  Users,
  ArrowRight,
  Filter as FilterIcon,
  Grid3X3,
  List,
  SlidersHorizontal,
  Minus,
  Plus,
  CheckCircle,
  Leaf,
  Package,
  Globe,
  BarChart3,
} from "lucide-react";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [sortBy, setSortBy] = useState("featured");
  const [viewMode, setViewMode] = useState("grid");
  const [showFilters, setShowFilters] = useState(false);
  const [priceRange, setPriceRange] = useState([0, 2000]);
  const [selectedDiets, setSelectedDiets] = useState([]);
  const navigate = useNavigate();
  const {
    addToCart,
    updateQuantity,
    isInCart,
    getItemQuantity,
    isItemUpdating,
  } = useCart();
  const { toast } = useToast();

  const categories = [
    {
      id: "all",
      name: "All Products",
      icon: "🛒",
      color: "from-slate-600 to-slate-800",
      description: "Complete product catalog",
    },
    {
      id: "grains",
      name: "Grains & Cereals",
      icon: "🌾",
      color: "from-amber-600 to-orange-700",
      description: "Premium whole grains",
    },
    {
      id: "seafood",
      name: "Fresh Seafood",
      icon: "🐟",
      color: "from-blue-600 to-indigo-700",
      description: "Ocean-fresh selection",
    },
    {
      id: "fruits",
      name: "Fresh Fruits",
      icon: "🍎",
      color: "from-red-600 to-pink-700",
      description: "Seasonal fruit varieties",
    },
    {
      id: "dairy",
      name: "Dairy Products",
      icon: "🥛",
      color: "from-blue-600 to-purple-700",
      description: "Pure dairy essentials",
    },
    {
      id: "nuts",
      name: "Nuts & Seeds",
      icon: "🥜",
      color: "from-amber-600 to-yellow-700",
      description: "Premium nut selection",
    },
    {
      id: "vegetables",
      name: "Fresh Vegetables",
      icon: "🥬",
      color: "from-green-600 to-emerald-700",
      description: "Farm-fresh produce",
    },
  ];

  const dietOptions = [
    { value: "vegan", label: "Vegan", icon: "🌱" },
    { value: "vegetarian", label: "Vegetarian", icon: "🥗" },
    { value: "keto", label: "Keto", icon: "🥑" },
    { value: "paleo", label: "Paleo", icon: "🦴" },
    { value: "mediterranean", label: "Mediterranean", icon: "🫒" },
    { value: "dash", label: "DASH", icon: "❤️" },
    { value: "gluten-free", label: "Gluten-Free", icon: "🌾" },
  ];

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    filterAndSortProducts();
  }, [
    products,
    searchTerm,
    selectedCategory,
    sortBy,
    priceRange,
    selectedDiets,
  ]);

  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:4000/api/products");
      const data = await response.json();
      setProducts(data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching products:", error);
      setLoading(false);
    }
  };

  const filterAndSortProducts = () => {
    let filtered = products.filter((product) => {
      const matchesSearch =
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory =
        selectedCategory === "all" ||
        product.category.toLowerCase() === selectedCategory.toLowerCase();
      const matchesPrice =
        product.price >= priceRange[0] && product.price <= priceRange[1];
      const matchesDiet =
        selectedDiets.length === 0 ||
        selectedDiets.some((diet) => product.dietCompatible?.includes(diet));

      return matchesSearch && matchesCategory && matchesPrice && matchesDiet;
    });

    // Sort products
    switch (sortBy) {
      case "price-low":
        filtered.sort((a, b) => a.price - b.price);
        break;
      case "price-high":
        filtered.sort((a, b) => b.price - a.price);
        break;
      case "rating":
        filtered.sort((a, b) => b.rating - a.rating);
        break;
      case "name":
        filtered.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case "popularity":
        filtered.sort((a, b) => b.reviews - a.reviews);
        break;
      default:
        break;
    }

    setFilteredProducts(filtered);
  };

  const handleAddToCart = async (product) => {
    try {
      await addToCart(product._id, 1);
      toast({
        title: "Added to cart!",
        description: `${product.name} has been added to your cart.`,
        variant: "default",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to add item to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleQuantityChange = async (productId, newQuantity) => {
    try {
      if (newQuantity <= 0) {
        await updateQuantity(productId, 0);
      } else {
        await updateQuantity(productId, newQuantity);
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update quantity. Please try again.",
        variant: "destructive",
      });
    }
  };

  const getDiscountPercentage = (originalPrice, currentPrice) => {
    return Math.round(((originalPrice - currentPrice) / originalPrice) * 100);
  };

  const clearAllFilters = () => {
    setSearchTerm("");
    setSelectedCategory("all");
    setPriceRange([0, 2000]);
    setSelectedDiets([]);
    setSortBy("featured");
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 via-gray-50 to-slate-100 flex items-center justify-center">
        <div className="text-center">
          <div className="relative">
            <div className="animate-spin rounded-full h-20 w-20 border-b-2 border-slate-600 mx-auto mb-6"></div>
            <div className="absolute inset-0 rounded-full border-2 border-slate-200 animate-pulse"></div>
          </div>
          <div className="space-y-2">
            <p className="text-xl font-semibold text-slate-700">
              Loading Product Catalog
            </p>
            <p className="text-slate-500">
              Preparing your premium shopping experience...
            </p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-gray-50 to-slate-100">
      {/* Professional Header Section */}
      <div className="relative overflow-hidden bg-gradient-to-r from-slate-800 via-slate-700 to-slate-900 text-white py-16">
        {/* Subtle Background Pattern */}
        <div className="absolute inset-0 opacity-10">
          <div className="absolute inset-0" style={{
            backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.1'%3E%3Ccircle cx='30' cy='30' r='2'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`,
          }}></div>
        </div>

        <div className="relative container mx-auto px-6 text-center">
          {/* Professional Badge */}
          <div className="inline-flex items-center gap-2 mb-6 px-4 py-2 bg-white/10 backdrop-blur-sm rounded-full border border-white/20">
            <Award className="w-4 h-4 text-amber-400" />
            <span className="text-sm font-medium text-slate-200">
              Premium Quality Assured
            </span>
          </div>

          <h1 className="text-4xl md:text-5xl font-bold mb-6 leading-tight tracking-tight">
            Premium Health &{" "}
            <span className="bg-gradient-to-r from-amber-400 to-orange-400 bg-clip-text text-transparent">
              Wellness Products
            </span>
          </h1>

          <p className="text-lg text-slate-300 mb-10 max-w-3xl mx-auto leading-relaxed">
            Curated selection of premium organic and nutritious products, 
            carefully sourced to support your health and wellness journey. 
            Every item meets our rigorous quality standards.
          </p>

          {/* Professional Search Bar */}
          <div className="max-w-2xl mx-auto relative">
            <div className="relative">
              <Search className="absolute left-5 top-1/2 transform -translate-y-1/2 text-slate-400 h-5 w-5" />
              <Input
                type="text"
                placeholder="Search products, categories, or dietary preferences..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-12 pr-4 py-4 text-base border-0 rounded-xl shadow-lg focus:ring-2 focus:ring-amber-500/50 bg-white/95 backdrop-blur-sm"
              />
            </div>
          </div>

          {/* Professional Stats */}
          <div className="flex justify-center gap-12 mt-12">
            <div className="text-center">
              <div className="text-2xl font-bold text-amber-400">{products.length}+</div>
              <div className="text-slate-300 text-sm font-medium">Premium Products</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-amber-400">100%</div>
              <div className="text-slate-300 text-sm font-medium">Quality Verified</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-amber-400">24h</div>
              <div className="text-slate-300 text-sm font-medium">Express Delivery</div>
            </div>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-6 py-12">
        {/* Professional Filter System */}
        <div className="bg-white rounded-2xl shadow-xl border border-slate-200/50 p-8 mb-12">
          <div className="flex items-center justify-between mb-8">
            <div className="flex items-center gap-3">
              <div className="p-2 bg-slate-100 rounded-lg">
                <FilterIcon className="w-5 h-5 text-slate-600" />
              </div>
              <div>
                <h2 className="text-xl font-bold text-slate-800">Advanced Filters</h2>
                <p className="text-sm text-slate-500">Refine your product selection</p>
              </div>
            </div>
            <Button
              variant="ghost"
              onClick={() => setShowFilters(!showFilters)}
              className="flex items-center gap-2 text-slate-600 hover:text-slate-800 hover:bg-slate-50"
            >
              <SlidersHorizontal className="w-4 h-4" />
              {showFilters ? "Hide" : "Show"} Advanced
            </Button>
          </div>

          {/* Basic Filters */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-8">
            {/* Category Filter */}
            <div>
              <label className="block text-sm font-semibold text-slate-700 mb-4">Product Categories</label>
              <div className="flex flex-wrap gap-3">
                {categories.map((category) => (
                  <button
                    key={category.id}
                    onClick={() => setSelectedCategory(category.id)}
                    className={`group relative px-4 py-3 rounded-lg text-sm font-medium transition-all duration-200 flex items-center gap-2 ${
                      selectedCategory === category.id
                        ? `bg-gradient-to-r ${category.color} text-white shadow-md`
                        : "bg-slate-100 text-slate-700 hover:bg-slate-200 hover:shadow-sm"
                    }`}
                  >
                    <span className="text-lg">{category.icon}</span>
                    <span>{category.name}</span>
                    {selectedCategory === category.id && (
                      <CheckCircle className="w-4 h-4 ml-1" />
                    )}
                  </button>
                ))}
              </div>
            </div>

            {/* Sort Options */}
            <div>
              <label className="block text-sm font-semibold text-slate-700 mb-4">Sort By</label>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-amber-500 bg-white"
              >
                <option value="featured">⭐ Featured</option>
                <option value="popularity">🔥 Most Popular</option>
                <option value="rating">⭐ Highest Rated</option>
                <option value="price-low">💰 Price: Low to High</option>
                <option value="price-high">💰 Price: High to Low</option>
                <option value="name">📝 Name A-Z</option>
              </select>
            </div>

            {/* View Mode */}
            <div>
              <label className="block text-sm font-semibold text-slate-700 mb-4">Display Mode</label>
              <div className="flex bg-slate-100 rounded-lg p-1">
                <button
                  onClick={() => setViewMode("grid")}
                  className={`p-3 rounded-md transition-all ${
                    viewMode === "grid"
                      ? "bg-white text-slate-800 shadow-sm"
                      : "text-slate-500 hover:text-slate-700"
                  }`}
                >
                  <Grid3X3 className="w-5 h-5" />
                </button>
                <button
                  onClick={() => setViewMode("list")}
                  className={`p-3 rounded-md transition-all ${
                    viewMode === "list"
                      ? "bg-white text-slate-800 shadow-sm"
                      : "text-slate-500 hover:text-slate-700"
                  }`}
                >
                  <List className="w-5 h-5" />
                </button>
              </div>
            </div>
          </div>

          {/* Advanced Filters */}
          {showFilters && (
            <div className="border-t border-slate-200 pt-8 space-y-8">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                {/* Price Range */}
                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-4">
                    Price Range: ₹{priceRange[0]} - ₹{priceRange[1]}
                  </label>
                  <div className="space-y-3">
                    <input
                      type="range"
                      min="0"
                      max="2000"
                      value={priceRange[1]}
                      onChange={(e) =>
                        setPriceRange([priceRange[0], parseInt(e.target.value)])
                      }
                      className="w-full h-2 bg-slate-200 rounded-lg appearance-none cursor-pointer"
                    />
                    <div className="flex justify-between text-xs text-slate-500">
                      <span>₹0</span>
                      <span>₹2000+</span>
                    </div>
                  </div>
                </div>

                {/* Diet Preferences */}
                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-4">Dietary Preferences</label>
                  <div className="flex flex-wrap gap-2">
                    {dietOptions.map((diet) => (
                      <button
                        key={diet.value}
                        onClick={() => {
                          setSelectedDiets((prev) =>
                            prev.includes(diet.value)
                              ? prev.filter((d) => d !== diet.value)
                              : [...prev, diet.value]
                          );
                        }}
                        className={`px-3 py-2 rounded-lg text-sm font-medium transition-all ${
                          selectedDiets.includes(diet.value)
                            ? "bg-amber-100 text-amber-800 border border-amber-300"
                            : "bg-slate-100 text-slate-700 hover:bg-slate-200"
                        }`}
                      >
                        <span className="mr-1">{diet.icon}</span>
                        {diet.label}
                      </button>
                    ))}
                  </div>
                </div>
              </div>

              {/* Clear Filters */}
              <div className="flex justify-center">
                <Button
                  onClick={clearAllFilters}
                  variant="outline"
                  className="border-slate-300 text-slate-600 hover:bg-slate-50 hover:border-slate-400"
                >
                  <Target className="w-4 h-4 mr-2" />
                  Clear All Filters
                </Button>
              </div>
            </div>
          )}
        </div>

        {/* Results Summary */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <p className="text-slate-600 text-lg">
                Showing{" "}
                <span className="font-bold text-slate-800 text-xl">
                  {filteredProducts.length}
                </span>{" "}
                products
                {searchTerm && (
                  <>
                    {" "}
                    for "
                    <span className="font-semibold text-amber-600">
                      {searchTerm}
                    </span>
                    "
                  </>
                )}
                {selectedCategory !== "all" && (
                  <>
                    {" "}
                    in{" "}
                    <span className="font-semibold text-slate-700">
                      {categories.find((c) => c.id === selectedCategory)?.name}
                    </span>
                  </>
                )}
              </p>
              {(searchTerm ||
                selectedCategory !== "all" ||
                selectedDiets.length > 0) && (
                <span className="px-3 py-1 bg-amber-100 text-amber-800 text-sm rounded-full font-medium">
                  Filtered Results
                </span>
              )}
            </div>

            <div className="flex items-center gap-2 text-sm text-slate-500">
              <Clock className="w-4 h-4" />
              <span>Last updated: {new Date().toLocaleTimeString()}</span>
            </div>
          </div>
        </div>

        {/* Products Grid/List */}
        {filteredProducts.length === 0 ? (
          <div className="text-center py-20">
            <div className="relative mb-8">
              <div className="text-6xl mb-4">🔍</div>
              <div className="absolute -top-2 -right-2 w-6 h-6 bg-amber-500 rounded-full animate-bounce"></div>
            </div>
            <h3 className="text-2xl font-bold text-slate-700 mb-4">
              No products found
            </h3>
            <p className="text-slate-500 mb-8 text-lg max-w-md mx-auto">
              Try adjusting your search criteria or filters to discover our premium product selection.
            </p>
            <div className="flex gap-4 justify-center">
              <Button
                onClick={clearAllFilters}
                className="bg-slate-800 hover:bg-slate-900 text-white"
              >
                <Target className="w-4 h-4 mr-2" />
                Clear All Filters
              </Button>
              <Button
                variant="outline"
                onClick={() => navigate("/")}
                className="border-slate-300 hover:border-slate-400"
              >
                <ArrowRight className="w-4 h-4 mr-2" />
                Explore Homepage
              </Button>
            </div>
          </div>
        ) : (
          <div
            className={
              viewMode === "grid"
                ? "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8"
                : "space-y-6"
            }
          >
            {filteredProducts.map((product, index) => (
              <Card
                key={product._id}
                className="group overflow-hidden transition-all duration-300 hover:shadow-xl hover:scale-[1.02] border border-slate-200/50 bg-white"
                style={{
                  animationDelay: `${index * 100}ms`,
                }}
              >
                {/* Product Image */}
                <div className="relative overflow-hidden bg-gradient-to-br from-slate-50 to-slate-100 p-8">
                  <div className="text-6xl text-center mb-4 group-hover:scale-105 transition-transform duration-300">
                    {product.image}
                  </div>

                  {/* Professional Badges */}
                  <div className="absolute top-3 left-3 flex flex-col gap-2">
                    {product.originalPrice > product.price && (
                      <div className="bg-gradient-to-r from-red-600 to-red-700 text-white text-xs font-bold px-3 py-1 rounded-lg shadow-md">
                        -{getDiscountPercentage(product.originalPrice, product.price)}%
                      </div>
                    )}
                    {product.tags?.includes("Organic") && (
                      <div className="bg-gradient-to-r from-green-600 to-green-700 text-white text-xs font-bold px-3 py-1 rounded-lg flex items-center gap-1 shadow-md">
                        <Leaf className="w-3 h-3" />
                        Organic
                      </div>
                    )}
                  </div>

                  {/* Quick Actions */}
                  <div className="absolute top-3 right-3 flex flex-col gap-2 opacity-0 group-hover:opacity-100 transition-all duration-300 transform translate-y-2 group-hover:translate-y-0">
                    <button className="bg-white/90 hover:bg-white p-2 rounded-lg shadow-lg transition-all hover:scale-110 hover:shadow-xl">
                      <Heart className="w-4 h-4 text-red-500" />
                    </button>
                    <button
                      onClick={() => navigate(`/products/${product._id}`)}
                      className="bg-white/90 hover:bg-white p-2 rounded-lg shadow-lg transition-all hover:scale-110 hover:shadow-xl"
                    >
                      <Eye className="w-4 h-4 text-slate-600" />
                    </button>
                  </div>

                  {/* Stock Status */}
                  {!product.inStock && (
                    <div className="absolute bottom-3 left-3 bg-red-600 text-white text-xs font-bold px-3 py-1 rounded-lg">
                      Out of Stock
                    </div>
                  )}

                  {/* Category Badge */}
                  <div className="absolute bottom-3 right-3">
                    <span className="px-3 py-1 bg-slate-800/80 text-white text-xs font-medium rounded-lg backdrop-blur-sm">
                      {product.category}
                    </span>
                  </div>
                </div>

                {/* Product Info */}
                <div className="p-6">
                  {/* Product Name */}
                  <h3 className="text-lg font-bold text-slate-800 mb-3 line-clamp-2 group-hover:text-amber-600 transition-colors duration-300">
                    {product.name}
                  </h3>

                  {/* Description */}
                  <p className="text-slate-600 text-sm mb-4 line-clamp-2 leading-relaxed">
                    {product.description}
                  </p>

                  {/* Rating */}
                  <div className="flex items-center gap-2 mb-4">
                    <div className="flex items-center">
                      {[...Array(5)].map((_, i) => (
                        <Star
                          key={i}
                          className={`w-4 h-4 ${
                            i < Math.floor(product.rating)
                              ? "text-amber-400 fill-current"
                              : "text-slate-300"
                          }`}
                        />
                      ))}
                    </div>
                    <span className="text-sm text-slate-600">
                      ({product.reviews})
                    </span>
                    <span className="ml-auto text-xs text-slate-500 bg-slate-100 px-2 py-1 rounded-full">
                      {product.rating}/5
                    </span>
                  </div>

                  {/* Price */}
                  <div className="flex items-center gap-3 mb-4">
                    <span className="text-2xl font-bold text-slate-800">
                      ₹{product.price}
                    </span>
                    {product.originalPrice > product.price && (
                      <span className="text-lg text-slate-400 line-through">
                        ₹{product.originalPrice}
                      </span>
                    )}
                  </div>

                  {/* Supplier Info */}
                  <div className="flex items-center gap-2 mb-4 text-sm text-slate-500">
                    <Package className="w-4 h-4 text-slate-400" />
                    <span className="truncate">{product.supplier}</span>
                  </div>

                  {/* Cart Actions */}
                  <div className="space-y-3">
                    {isInCart(product._id) ? (
                      <div className="bg-amber-50 border border-amber-200 rounded-xl p-3">
                        <div className="flex items-center gap-2 mb-2">
                          <div className="w-2 h-2 bg-amber-500 rounded-full animate-pulse"></div>
                          <span className="text-sm font-medium text-amber-800">
                            In Cart: {getItemQuantity(product._id)} × ₹
                            {product.price}
                          </span>
                        </div>
                        <div className="flex items-center gap-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() =>
                              handleQuantityChange(
                                product._id,
                                getItemQuantity(product._id) - 1
                              )
                            }
                            disabled={isItemUpdating(product._id)}
                            className="flex-1 border-amber-300 text-amber-700 hover:bg-amber-100"
                          >
                            <Minus className="w-4 h-4" />
                          </Button>
                          <span className="text-center font-semibold min-w-[2rem]">
                            {getItemQuantity(product._id)}
                          </span>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() =>
                              handleQuantityChange(
                                product._id,
                                getItemQuantity(product._id) + 1
                              )
                            }
                            disabled={isItemUpdating(product._id)}
                            className="flex-1 border-amber-300 text-amber-700 hover:bg-amber-100"
                          >
                            <Plus className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    ) : (
                      <Button
                        onClick={() => handleAddToCart(product)}
                        disabled={!product.inStock}
                        className="w-full bg-slate-800 hover:bg-slate-900 text-white border-0 shadow-lg hover:shadow-xl transition-all duration-300"
                      >
                        <ShoppingCart className="w-4 h-4 mr-2" />
                        Add to Cart
                      </Button>
                    )}

                    {/* View Details Button */}
                    <Button
                      variant="outline"
                      onClick={() => navigate(`/products/${product._id}`)}
                      className="w-full border-slate-300 text-slate-700 hover:bg-slate-50 hover:border-slate-400 transition-all duration-300"
                    >
                      <Eye className="w-4 h-4 mr-2" />
                      View Details
                    </Button>
                  </div>
                </div>
              </Card>
            ))}
          </div>
        )}

        {/* Professional Bottom CTA */}
        {filteredProducts.length > 0 && (
          <div className="mt-20 text-center">
            <div className="relative overflow-hidden bg-gradient-to-r from-slate-800 via-slate-700 to-slate-900 rounded-2xl p-12 text-white">
              {/* Subtle Background Pattern */}
              <div className="absolute inset-0 opacity-5">
                <div className="absolute inset-0" style={{
                  backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.1'%3E%3Ccircle cx='30' cy='30' r='2'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`,
                }}></div>
              </div>

              <div className="relative">
                <div className="flex items-center justify-center gap-2 mb-6">
                  <BarChart3 className="w-6 h-6 text-amber-400" />
                  <span className="text-sm font-medium bg-white/10 px-4 py-2 rounded-full border border-white/20">
                    Expert Consultation
                  </span>
                  <BarChart3 className="w-6 h-6 text-amber-400" />
                </div>

                <h3 className="text-3xl font-bold mb-6">
                  Need Professional Guidance?
                </h3>
                <p className="text-slate-300 mb-8 max-w-3xl mx-auto text-lg leading-relaxed">
                  Our certified nutrition experts are available to provide personalized 
                  recommendations based on your health goals and dietary preferences. 
                  Get professional advice to optimize your wellness journey.
                </p>

                <div className="flex flex-col sm:flex-row gap-4 justify-center">
                  <Button
                    variant="secondary"
                    size="lg"
                    className="bg-amber-500 hover:bg-amber-600 text-white shadow-lg hover:shadow-xl transition-all duration-300"
                  >
                    <TrendingUp className="w-5 h-5 mr-2" />
                    Get Personalized Recommendations
                  </Button>
                  <Button
                    variant="outline"
                    size="lg"
                    className="border-white/30 text-white hover:bg-white/10 backdrop-blur-sm"
                  >
                    <Users className="w-5 h-5 mr-2" />
                    Schedule Consultation
                  </Button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Custom CSS for slider */}
      <style jsx>{`
        .slider::-webkit-slider-thumb {
          appearance: none;
          height: 20px;
          width: 20px;
          border-radius: 50%;
          background: linear-gradient(to right, #475569, #1e293b);
          cursor: pointer;
          box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
        }

        .slider::-moz-range-thumb {
          height: 20px;
          width: 20px;
          border-radius: 50%;
          background: linear-gradient(to right, #475569, #1e293b);
          cursor: pointer;
          border: none;
          box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
        }
      `}</style>
    </div>
  );
};

export default ProductsPage;
