import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../components/ui/card';
import { Button } from '../components/ui/button';
import { Badge } from '../components/ui/badge';
import { Input } from '../components/ui/input';
import { useCart } from '../contexts/CartContext';
import { productsAPI } from '../services/api';

export default function ProductsPage() {
  const navigate = useNavigate();
  const { addToCart, getItemQuantity, isInCart } = useCart();

  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedDiet, setSelectedDiet] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  // Load products and categories on component mount
  useEffect(() => {
    loadProductsAndCategories();
  }, []);

  // Filter products when filters change
  useEffect(() => {
    filterProducts();
  }, [products, selectedCategory, selectedDiet, searchTerm]);

  const loadProductsAndCategories = async () => {
    try {
      setLoading(true);
      const [productsData, categoriesData] = await Promise.all([
        productsAPI.getAll(),
        productsAPI.getCategories()
      ]);

      setProducts(productsData);
      setCategories(categoriesData);
      setFilteredProducts(productsData);
    } catch (error) {
      console.error('Error loading products:', error);
    } finally {
      setLoading(false);
    }
  };

  const filterProducts = () => {
    let filtered = [...products];

    // Filter by category
    if (selectedCategory) {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    // Filter by diet
    if (selectedDiet) {
      filtered = filtered.filter(product => 
        product.dietCompatible.includes(selectedDiet.toLowerCase())
      );
    }

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setFilteredProducts(filtered);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category === selectedCategory ? '' : category);
  };

  const handleDietChange = (diet) => {
    setSelectedDiet(diet === selectedDiet ? '' : diet);
  };

  const handleAddToCart = async (productId) => {
    await addToCart(productId, 1);
  };

  const handleViewProduct = (productId) => {
    navigate(`/product/${productId}`);
  };

  const dietOptions = [
    { value: 'vegan', label: 'Vegan', icon: 'üå±' },
