import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../contexts/CartContext";
import { Button } from "../components/ui/button";
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { toast } from "../components/ui/use-toast";

export default function RecipesPage() {
  const [recipes, setRecipes] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState("all");
  const { addToCart, removeFromCart, updateQuantity, cart } = useCart();
  const navigate = useNavigate();

  // Fetch recipes and products from database
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        
        // Fetch recipes from database
        const recipesResponse = await fetch("http://localhost:4000/api/recipes");
        const recipesData = await recipesResponse.json();
        
        // Fetch products from database
        const productsResponse = await fetch("http://localhost:4000/api/products");
        const productsData = await productsResponse.json();
        
        setProducts(productsData);
        
        // Update recipes with cart status and product info
        const updatedRecipes = recipesData.map((recipe) => ({
          ...recipe,
          ingredients: recipe.ingredients.map((ingredient) => {
            // Find matching product by name
            const matchingProduct = productsData.find(
              (product) => product.name.toLowerCase() === ingredient.productName?.toLowerCase()
            );
            
            // Check if ingredient is in cart
            const cartItem = cart.items.find(
              (item) => item.productId === matchingProduct?._id
            );
            
            return {
              ...ingredient,
              inCart: !!cartItem,
              cartQuantity: cartItem ? cartItem.quantity : 0,
              available: !!matchingProduct,
              productId: matchingProduct?._id,
              price: matchingProduct?.price,
            };
          }),
        }));
        
        setRecipes(updatedRecipes);
      } catch (error) {
        console.error("Error fetching data:", error);
        toast({
          title: "Error",
          description: "Failed to load recipes. Please try again.",
          variant: "destructive",
        });
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [cart.items]);

  const handleAddIngredientToCart = async (ingredient) => {
    if (!ingredient.productId) {
      toast({
        title: "Ingredient Not Available",
        description: `${ingredient.name} is not available for purchase in our store.`,
        variant: "destructive",
      });
      return;
    }

    try {
      await addToCart(ingredient.productId, 1);

      // Update local state
      setRecipes((prev) =>
        prev.map((recipe) => ({
          ...recipe,
          ingredients: recipe.ingredients.map((item) =>
            item.name === ingredient.name && item.productName === ingredient.productName
              ? { ...item, inCart: true, cartQuantity: 1 }
              : item
          ),
        }))
      );

      toast({
        title: "Added to Cart",
        description: `${ingredient.name} has been added to your cart!`,
        variant: "default",
      });
    } catch (error) {
      console.error("Error adding ingredient to cart:", error);
      toast({
        title: "Error",
        description: "Failed to add ingredient to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleRemoveIngredientFromCart = async (ingredient) => {
    try {
      await removeFromCart(ingredient.productId);

      // Update local state
      setRecipes((prev) =>
        prev.map((recipe) => ({
          ...recipe,
          ingredients: recipe.ingredients.map((item) =>
            item.name === ingredient.name && item.productName === ingredient.productName
              ? { ...item, inCart: false, cartQuantity: 0 }
              : item
          ),
        }))
      );

      toast({
        title: "Removed from Cart",
        description: `${ingredient.name} has been removed from your cart!`,
        variant: "default",
      });
    } catch (error) {
      console.error("Error removing ingredient from cart:", error);
      toast({
        title: "Error",
        description: "Failed to remove ingredient from cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleUpdateIngredientQuantity = async (ingredient, newQuantity) => {
    if (newQuantity <= 0) {
      await handleRemoveIngredientFromCart(ingredient);
      return;
    }

    try {
      await updateQuantity(ingredient.productId, newQuantity);

      // Update local state
      setRecipes((prev) =>
        prev.map((recipe) => ({
          ...recipe,
          ingredients: recipe.ingredients.map((item) =>
            item.name === ingredient.name && item.productName === ingredient.productName
              ? { ...item, cartQuantity: newQuantity }
              : item
          ),
        }))
      );

      toast({
        title: "Quantity Updated",
        description: `${ingredient.name} quantity updated to ${newQuantity}!`,
        variant: "default",
      });
    } catch (error) {
      console.error("Error updating ingredient quantity:", error);
      toast({
        title: "Error",
        description: "Failed to update quantity. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleOrderMissingIngredients = async (recipe) => {
    try {
      const missingIngredients = recipe.ingredients.filter(
        (ingredient) =>
          !ingredient.inCart && ingredient.available && ingredient.productId
      );

      if (missingIngredients.length === 0) {
        toast({
          title: "All Ingredients Available",
          description: "All ingredients for this recipe are already in your cart!",
          variant: "default",
        });
        return;
      }

      // Add all missing ingredients to cart
      for (const ingredient of missingIngredients) {
        await addToCart(ingredient.productId, 1);
      }

      // Update local state
      setRecipes((prev) =>
        prev.map((r) =>
          r._id === recipe._id
            ? {
                ...r,
                ingredients: r.ingredients.map((item) => ({
                  ...item,
                  inCart: true,
                  cartQuantity: item.cartQuantity || 1,
                })),
              }
            : r
        )
      );

      toast({
        title: "Ingredients Added",
        description: `${missingIngredients.length} ingredients have been added to your cart!`,
        variant: "default",
      });

      // Navigate to cart
      navigate("/cart");
    } catch (error) {
      console.error("Error adding ingredients to cart:", error);
      toast({
        title: "Error",
        description: "Failed to add some ingredients to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleViewFullRecipe = (recipeId) => {
    navigate(`/recipes/${recipeId}`);
  };

  const renderIngredient = (ingredient) => {
    if (!ingredient) return null;
    
    const ingredientName = ingredient.productName || ingredient.name;
    if (!ingredientName) return null;
    
    const inCart = ingredient.inCart;
    const cartQuantity = ingredient.cartQuantity;
    const matchingProduct = products.find(
      (product) => product.name.toLowerCase() === ingredientName.toLowerCase()
    );
    const price = matchingProduct ? matchingProduct.price : ingredient.price || 0;

    return (
      <div
        key={ingredientName}
        className="flex items-center justify-between p-3 bg-gray-50 rounded-lg mb-2"
      >
        <div className="flex items-center space-x-3">
          <div className="w-2 h-2 bg-yellow-400 rounded-full"></div>
          <span className="font-medium">
            {ingredient.name || ingredientName}
          </span>
          <span className="text-gray-600">{ingredient.quantity}</span>
          <span className="text-green-600 font-semibold">₹{price}</span>
        </div>
        
        {inCart ? (
          <div className="flex items-center space-x-2">
            <span className="text-sm text-green-600 font-medium">
              In Cart: {cartQuantity} × ₹{price}
            </span>
            <div className="flex items-center space-x-1 bg-green-100 rounded-lg px-2 py-1">
              <button
                onClick={() =>
                  handleUpdateIngredientQuantity(
                    ingredient,
                    Math.max(0, cartQuantity - 1)
                  )
                }
                className="w-6 h-6 flex items-center justify-center text-green-600 hover:bg-green-200 rounded"
              >
                -
              </button>
              <span className="w-8 text-center font-medium">
                {cartQuantity}
              </span>
              <button
                onClick={() =>
                  handleUpdateIngredientQuantity(
                    ingredient,
                    cartQuantity + 1
                  )
                }
                className="w-6 h-6 flex items-center justify-center text-green-600 hover:bg-green-200 rounded"
              >
                +
              </button>
            </div>
            <button
              onClick={() => handleRemoveIngredientFromCart(ingredient)}
              className="text-red-500 hover:text-red-700 text-sm font-medium"
            >
              Remove
            </button>
          </div>
        ) : (
          <button
            onClick={() => handleAddIngredientToCart(ingredient)}
            className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-colors"
          >
            ADD TO CART
          </button>
        )}
      </div>
    );
  };

  const filteredRecipes = selectedCategory === "all" 
    ? recipes 
    : recipes.filter(recipe => recipe.difficulty.toLowerCase() === selectedCategory.toLowerCase());

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading recipes...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Healthy Recipes</h1>
              <p className="text-gray-600 mt-2">
                Discover delicious and nutritious recipes for your healthy lifestyle
              </p>
            </div>
            <Button
              onClick={() => navigate("/cart")}
              className="bg-green-500 hover:bg-green-600"
            >
              🛒 View Cart ({cart.items.length})
            </Button>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Category Filter */}
        <div className="mb-8">
          <div className="flex flex-wrap gap-2">
            <button
              onClick={() => setSelectedCategory("all")}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                selectedCategory === "all"
                  ? "bg-green-500 text-white"
                  : "bg-gray-200 text-gray-700 hover:bg-gray-300"
              }`}
            >
              All Recipes
            </button>
            <button
              onClick={() => setSelectedCategory("easy")}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                selectedCategory === "easy"
                  ? "bg-green-500 text-white"
                  : "bg-gray-200 text-gray-700 hover:bg-gray-300"
              }`}
            >
              Easy
            </button>
            <button
              onClick={() => setSelectedCategory("medium")}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                selectedCategory === "medium"
                  ? "bg-green-500 text-white"
                  : "bg-gray-200 text-gray-700 hover:bg-gray-300"
              }`}
            >
              Medium
            </button>
            <button
              onClick={() => setSelectedCategory("hard")}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                selectedCategory === "hard"
                  ? "bg-green-500 text-white"
                  : "bg-gray-200 text-gray-700 hover:bg-gray-300"
              }`}
            >
              Hard
            </button>
          </div>
        </div>

        {/* Recipes Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {filteredRecipes.map((recipe) => (
            <Card key={recipe._id} className="overflow-hidden">
              <CardHeader className="bg-gradient-to-r from-green-500 to-blue-500 text-white">
                <div className="flex items-center space-x-3">
                  <div className="text-4xl">{recipe.image}</div>
                  <div>
                    <CardTitle className="text-xl">{recipe.name}</CardTitle>
                    <p className="text-green-100 text-sm">{recipe.description}</p>
                  </div>
                </div>
              </CardHeader>
              
              <CardContent className="p-6">
                {/* Recipe Info */}
                <div className="flex flex-wrap gap-2 mb-4">
                  <Badge variant="secondary" className="text-xs">
                    ⏰ {recipe.cookingTime}
                  </Badge>
                  <Badge variant="secondary" className="text-xs">
                    👥 {recipe.servings} servings
                  </Badge>
                  <Badge variant="secondary" className="text-xs">
                    🔥 {recipe.calories} cal
                  </Badge>
                  <Badge variant="secondary" className="text-xs">
                    📊 {recipe.difficulty}
                  </Badge>
                </div>

                {/* Tags */}
                <div className="flex flex-wrap gap-2 mb-4">
                  {recipe.tags &&
                    recipe.tags.map((tag, index) => (
                      <Badge key={index} variant="outline" className="text-xs">
                        {tag}
                      </Badge>
                    ))}
                </div>

                {/* Ingredients */}
                <div className="mb-6">
                  <h4 className="text-lg font-semibold text-gray-900 mb-3">
                    Ingredients
                  </h4>
                  <div className="space-y-2">
                    {recipe.ingredients &&
                      recipe.ingredients
                        .filter(
                          (ingredient) =>
                            ingredient &&
                            (ingredient.productName || ingredient.name)
                        )
                        .map((ingredient) => renderIngredient(ingredient))}
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-3">
                  <Button
                    onClick={() => handleViewFullRecipe(recipe._id)}
                    variant="outline"
                    className="flex-1"
                  >
                    VIEW FULL RECIPE
                  </Button>
                  <Button
                    onClick={() => handleOrderMissingIngredients(recipe)}
                    className="flex-1 bg-green-500 hover:bg-green-600"
                    disabled={recipe.ingredients.every(
                      (ingredient) =>
                        ingredient.inCart ||
                        !ingredient.available ||
                        !ingredient.productId
                    )}
                  >
                    ORDER MISSING INGREDIENTS
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </div>
  );
}
