import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../contexts/CartContext";
import { useToast } from "../components/ui/use-toast";
import { Button } from "../components/ui/button";
import { Card } from "../components/ui/card";
import { Input } from "../components/ui/input";
import {
  Search,
  Filter,
  Star,
  ShoppingCart,
  Heart,
  Eye,
  TrendingUp,
  Award,
  Truck,
  Shield,
  Sparkles,
  Zap,
  Target,
  Globe,
  Leaf,
  Clock,
  Users,
  ArrowRight,
  Filter as FilterIcon,
  Grid3X3,
  List,
  SlidersHorizontal,
  Minus,
  Plus,
} from "lucide-react";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [sortBy, setSortBy] = useState("featured");
  const [viewMode, setViewMode] = useState("grid");
  const [showFilters, setShowFilters] = useState(false);
  const [priceRange, setPriceRange] = useState([0, 2000]);
  const [selectedDiets, setSelectedDiets] = useState([]);
  const navigate = useNavigate();
  const {
    addToCart,
    updateQuantity,
    isInCart,
    getItemQuantity,
    isItemUpdating,
  } = useCart();
  const { toast } = useToast();

  const categories = [
    {
      id: "all",
      name: "All Products",
      icon: "ðŸ›’",
      color: "from-blue-500 to-cyan-500",
      description: "Explore our complete collection",
    },
    {
      id: "grains",
      name: "Grains & Cereals",
      icon: "ðŸŒ¾",
      color: "from-amber-500 to-orange-500",
      description: "Nutritious whole grains",
    },
    {
      id: "seafood",
      name: "Fresh Seafood",
      icon: "ðŸŸ",
      color: "from-blue-500 to-indigo-500",
      description: "Premium ocean delights",
    },
    {
      id: "fruits",
      name: "Fresh Fruits",
      icon: "ðŸŽ",
      color: "from-red-500 to-pink-500",
      description: "Nature's sweet treats",
    },
    {
      id: "dairy",
      name: "Dairy Products",
      icon: "ðŸ¥›",
      color: "from-blue-500 to-purple-500",
      description: "Pure dairy goodness",
    },
    {
      id: "nuts",
      name: "Nuts & Seeds",
      icon: "ðŸ¥œ",
      color: "from-amber-500 to-yellow-500",
      description: "Healthy energy boosters",
    },
    {
      id: "vegetables",
      name: "Fresh Vegetables",
      icon: "ðŸ¥¬",
      color: "from-green-500 to-emerald-500",
      description: "Farm-fresh produce",
    },
  ];

  const dietOptions = [
    { value: "vegan", label: "Vegan", icon: "ðŸŒ±" },
    { value: "vegetarian", label: "Vegetarian", icon: "ðŸ¥—" },
    { value: "keto", label: "Keto", icon: "ðŸ¥‘" },
    { value: "paleo", label: "Paleo", icon: "ðŸ¦´" },
    { value: "mediterranean", label: "Mediterranean", icon: "ðŸ«’" },
    { value: "dash", label: "DASH", icon: "â¤ï¸" },
    { value: "gluten-free", label: "Gluten-Free", icon: "ðŸŒ¾" },
  ];

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    filterAndSortProducts();
  }, [products, searchTerm, selectedCategory, sortBy, priceRange, selectedDiets]);

  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:4000/api/products");
      const data = await response.json();
      setProducts(data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching products:", error);
      setLoading(false);
    }
  };

  const filterAndSortProducts = () => {
    let filtered = products.filter((product) => {
      const matchesSearch =
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory =
        selectedCategory === "all" ||
        product.category.toLowerCase() === selectedCategory.toLowerCase();
      const matchesPrice = product.price >= priceRange[0] && product.price <= priceRange[1];
      const matchesDiet = selectedDiets.length === 0 || 
        selectedDiets.some(diet => product.dietCompatible?.includes(diet));

      return matchesSearch && matchesCategory && matchesPrice && matchesDiet;
    });

    // Sort products
    switch (sortBy) {
      case "price-low":
        filtered.sort((a, b) => a.price - b.price);
        break;
      case "price-high":
        filtered.sort((a, b) => b.price - a.price);
        break;
      case "rating":
        filtered.sort((a, b) => b.rating - a.rating);
        break;
      case "name":
        filtered.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case "popularity":
        filtered.sort((a, b) => b.reviews - a.reviews);
        break;
      default:
        break;
    }

    setFilteredProducts(filtered);
  };

  const handleAddToCart = async (product) => {
    try {
      await addToCart(product._id, 1);
      toast({
        title: "Added to cart!",
        description: `${product.name} has been added to your cart.`,
        variant: "default",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to add item to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleQuantityChange = async (productId, newQuantity) => {
    try {
      if (newQuantity <= 0) {
        await updateQuantity(productId, 0);
      } else {
        await updateQuantity(productId, newQuantity);
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update quantity. Please try again.",
        variant: "destructive",
      });
    }
  };

  const getDiscountPercentage = (originalPrice, currentPrice) => {
    return Math.round(((originalPrice - currentPrice) / originalPrice) * 100);
  };

  const clearAllFilters = () => {
    setSearchTerm("");
    setSelectedCategory("all");
    setPriceRange([0, 2000]);
    setSelectedDiets([]);
    setSortBy("featured");
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 via-blue-50 to-purple-50 flex items-center justify-center">
        <div className="text-center">
          <div className="relative">
            <div className="animate-spin rounded-full h-20 w-20 border-b-2 border-green-600 mx-auto mb-6"></div>
            <div className="absolute inset-0 rounded-full border-2 border-green-200 animate-pulse"></div>
          </div>
          <div className="space-y-2">
            <p className="text-xl font-semibold text-gray-700">Loading Amazing Products</p>
            <p className="text-gray-500">Preparing your healthy shopping experience...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 via-blue-50 to-purple-50">
      {/* Hero Section */}
      <div className="relative overflow-hidden bg-gradient-to-r from-green-600 via-blue-600 to-purple-600 text-white py-20">
        {/* Background Elements */}
        <div className="absolute inset-0">
          <div className="absolute top-0 left-0 w-72 h-72 bg-white/10 rounded-full mix-blend-multiply filter blur-xl animate-blob"></div>
          <div className="absolute top-0 right-0 w-72 h-72 bg-white/10 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-2000"></div>
          <div className="absolute bottom-0 left-1/2 w-72 h-72 bg-white/10 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-4000"></div>
        </div>

        <div className="relative container mx-auto px-4 text-center">
          <div className="flex items-center justify-center gap-2 mb-4">
            <Sparkles className="w-6 h-6 text-yellow-300 animate-pulse" />
            <span className="text-sm font-medium bg-white/20 px-3 py-1 rounded-full">
              Fresh & Organic
            </span>
            <Sparkles className="w-6 h-6 text-yellow-300 animate-pulse" />
          </div>

          <h1 className="text-5xl md:text-6xl font-bold mb-6 leading-tight">
            Discover Fresh &{" "}
            <span className="bg-gradient-to-r from-yellow-300 to-orange-300 bg-clip-text text-transparent">
              Healthy Products
            </span>
          </h1>

          <p className="text-xl text-green-100 mb-10 max-w-3xl mx-auto leading-relaxed">
            From farm to table, we bring you the finest organic and nutritious
            products to support your healthy lifestyle journey. Every item is
            carefully selected for quality and freshness.
          </p>

          {/* Search Bar */}
          <div className="max-w-3xl mx-auto relative">
            <div className="relative">
              <Search className="absolute left-6 top-1/2 transform -translate-y-1/2 text-gray-400 h-6 w-6" />
              <Input
                type="text"
                placeholder="Search for products, categories, or dietary preferences..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-16 pr-6 py-5 text-lg border-0 rounded-full shadow-2xl focus:ring-4 focus:ring-green-300/50 bg-white/95 backdrop-blur-sm"
              />
              <Button className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gradient-to-r from-green-500 to-blue-500 hover:from-green-600 hover:to-blue-600 text-white px-6 py-2 rounded-full shadow-lg">
                <Zap className="w-4 h-4 mr-2" />
                Search
              </Button>
            </div>
          </div>

          {/* Quick Stats */}
          <div className="flex justify-center gap-8 mt-12">
            <div className="text-center">
              <div className="text-3xl font-bold text-yellow-300">{products.length}+</div>
              <div className="text-green-100 text-sm">Products</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-yellow-300">100%</div>
              <div className="text-green-100 text-sm">Organic</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-yellow-300">24h</div>
              <div className="text-green-100 text-sm">Delivery</div>
            </div>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-12">
        {/* Advanced Filters */}
        <div className="bg-white/80 backdrop-blur-sm rounded-3xl shadow-2xl p-8 mb-12 border border-white/20">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <FilterIcon className="w-6 h-6 text-green-600" />
              <h2 className="text-2xl font-bold text-gray-800">Smart Filters</h2>
            </div>
            <Button
              variant="ghost"
              onClick={() => setShowFilters(!showFilters)}
              className="flex items-center gap-2 text-green-600 hover:text-green-700"
            >
              <SlidersHorizontal className="w-4 h-4" />
              {showFilters ? "Hide" : "Show"} Advanced
            </Button>
          </div>

          {/* Basic Filters */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-6">
            {/* Category Filter */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-3">Categories</label>
              <div className="flex flex-wrap gap-2">
                {categories.map((category) => (
                  <button
                    key={category.id}
                    onClick={() => setSelectedCategory(category.id)}
                    className={`group relative px-4 py-2 rounded-full text-sm font-medium transition-all duration-300 flex items-center gap-2 ${
                      selectedCategory === category.id
                        ? `bg-gradient-to-r ${category.color} text-white shadow-lg scale-105`
                        : "bg-gray-100 text-gray-700 hover:bg-gray-200 hover:scale-105"
                    }`}
                  >
                    <span className="text-lg group-hover:scale-110 transition-transform">{category.icon}</span>
                    <span>{category.name}</span>
                    {selectedCategory === category.id && (
                      <div className="absolute -top-1 -right-1 w-3 h-3 bg-white rounded-full animate-pulse"></div>
                    )}
                  </button>
                ))}
              </div>
            </div>

            {/* Sort Options */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-3">Sort By</label>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent bg-white/80 backdrop-blur-sm"
              >
                <option value="featured">âœ¨ Featured</option>
                <option value="popularity">ðŸ”¥ Most Popular</option>
                <option value="rating">â­ Highest Rated</option>
                <option value="price-low">ðŸ’° Price: Low to High</option>
                <option value="price-high">ðŸ’° Price: High to Low</option>
                <option value="name">ðŸ“ Name A-Z</option>
              </select>
            </div>

            {/* View Mode */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-3">View Mode</label>
              <div className="flex bg-gray-100 rounded-xl p-1">
                <button
                  onClick={() => setViewMode("grid")}
                  className={`p-3 rounded-lg transition-all ${
                    viewMode === "grid"
                      ? "bg-white text-green-600 shadow-md scale-105"
                      : "text-gray-500 hover:text-gray-700 hover:scale-105"
                  }`}
                >
                  <Grid3X3 className="w-5 h-5" />
                </button>
                <button
                  onClick={() => setViewMode("list")}
                  className={`p-3 rounded-lg transition-all ${
                    viewMode === "list"
                      ? "bg-white text-green-600 shadow-md scale-105"
                      : "text-gray-500 hover:text-gray-700 hover:scale-105"
                  }`}
                >
                  <List className="w-5 h-5" />
                </button>
              </div>
            </div>
          </div>

          {/* Advanced Filters */}
          {showFilters && (
            <div className="border-t border-gray-200 pt-6 space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* Price Range */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">
                    Price Range: â‚¹{priceRange[0]} - â‚¹{priceRange[1]}
                  </label>
                  <div className="space-y-2">
                    <input
                      type="range"
                      min="0"
                      max="2000"
                      value={priceRange[1]}
                      onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
                      className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer slider"
                    />
                    <div className="flex justify-between text-xs text-gray-500">
                      <span>â‚¹0</span>
                      <span>â‚¹2000+</span>
                    </div>
                  </div>
                </div>

                {/* Diet Preferences */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">Diet Preferences</label>
                  <div className="flex flex-wrap gap-2">
                    {dietOptions.map((diet) => (
                      <button
                        key={diet.value}
                        onClick={() => {
                          setSelectedDiets(prev =>
                            prev.includes(diet.value)
                              ? prev.filter(d => d !== diet.value)
                              : [...prev, diet.value]
                          );
                        }}
                        className={`px-3 py-2 rounded-lg text-sm font-medium transition-all ${
                          selectedDiets.includes(diet.value)
                            ? "bg-green-100 text-green-800 border-2 border-green-300 scale-105"
                            : "bg-gray-100 text-gray-700 hover:bg-gray-200 hover:scale-105"
                        }`}
                      >
                        <span className="mr-1">{diet.icon}</span>
                        {diet.label}
                      </button>
                    ))}
                  </div>
                </div>
              </div>

              {/* Clear Filters */}
              <div className="flex justify-center">
                <Button
                  onClick={clearAllFilters}
                  variant="outline"
                  className="border-red-200 text-red-600 hover:bg-red-50 hover:border-red-300"
                >
                  <Target className="w-4 h-4 mr-2" />
                  Clear All Filters
                </Button>
              </div>
            </div>
          )}
        </div>

        {/* Results Count */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <p className="text-gray-600 text-lg">
                Showing{" "}
                <span className="font-bold text-green-600 text-xl">
                  {filteredProducts.length}
                </span>{" "}
                products
                {searchTerm && (
                  <>
                    {" "}
                    for "<span className="font-semibold text-blue-600">{searchTerm}</span>"
                  </>
                )}
                {selectedCategory !== "all" && (
                  <>
                    {" "}
                    in{" "}
                    <span className="font-semibold text-purple-600">
                      {categories.find((c) => c.id === selectedCategory)?.name}
                    </span>
                  </>
                )}
              </p>
              {(searchTerm || selectedCategory !== "all" || selectedDiets.length > 0) && (
                <span className="px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full font-medium">
                  Filtered Results
                </span>
              )}
            </div>

            <div className="flex items-center gap-2 text-sm text-gray-500">
              <Clock className="w-4 h-4" />
              <span>Last updated: {new Date().toLocaleTimeString()}</span>
            </div>
          </div>
        </div>

        {/* Products Grid/List */}
        {filteredProducts.length === 0 ? (
          <div className="text-center py-20">
            <div className="relative mb-8">
              <div className="text-8xl mb-4">ðŸ”</div>
              <div className="absolute -top-2 -right-2 w-6 h-6 bg-red-500 rounded-full animate-bounce"></div>
            </div>
            <h3 className="text-3xl font-bold text-gray-700 mb-4">No products found</h3>
            <p className="text-gray-500 mb-8 text-lg max-w-md mx-auto">
              Try adjusting your search criteria or filters to discover more amazing products.
            </p>
            <div className="flex gap-4 justify-center">
              <Button
                onClick={clearAllFilters}
                className="bg-gradient-to-r from-green-600 to-blue-600 hover:from-green-700 hover:to-blue-700"
              >
                <Target className="w-4 h-4 mr-2" />
                Clear All Filters
              </Button>
              <Button
                variant="outline"
                onClick={() => navigate("/")}
                className="border-gray-300 hover:border-gray-400"
              >
                <ArrowRight className="w-4 h-4 mr-2" />
                Explore Homepage
              </Button>
            </div>
          </div>
        ) : (
          <div
            className={
              viewMode === "grid"
                ? "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8"
                : "space-y-6"
            }
          >
            {filteredProducts.map((product, index) => (
              <Card
                key={product._id}
                className="group overflow-hidden transition-all duration-500 hover:shadow-2xl hover:scale-105 border-0 bg-white/90 backdrop-blur-sm relative"
                style={{
                  animationDelay: `${index * 100}ms`,
                }}
              >
                {/* Product Image */}
                <div className="relative overflow-hidden bg-gradient-to-br from-green-100 via-blue-100 to-purple-100 p-8">
                  <div className="text-7xl text-center mb-4 group-hover:scale-110 transition-transform duration-300">
                    {product.image}
                  </div>

                  {/* Animated Background */}
                  <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -skew-x-12 -translate-x-full group-hover:translate-x-full transition-transform duration-1000"></div>

                  {/* Badges */}
                  <div className="absolute top-3 left-3 flex flex-col gap-2">
                    {product.originalPrice > product.price && (
                      <div className="bg-gradient-to-r from-red-500 to-pink-500 text-white text-xs font-bold px-3 py-1 rounded-full shadow-lg animate-pulse">
                        -{getDiscountPercentage(product.originalPrice, product.price)}%
                      </div>
                    )}
                    {product.tags?.includes("Organic") && (
                      <div className="bg-gradient-to-r from-green-500 to-emerald-500 text-white text-xs font-bold px-3 py-1 rounded-full flex items-center gap-1 shadow-lg">
                        <Leaf className="w-3 h-3" />
                        Organic
                      </div>
                    )}
                  </div>

                  {/* Quick Actions */}
                  <div className="absolute top-3 right-3 flex flex-col gap-2 opacity-0 group-hover:opacity-100 transition-all duration-300 transform translate-y-2 group-hover:translate-y-0">
                    <button className="bg-white/90 hover:bg-white p-2 rounded-full shadow-lg transition-all hover:scale-110 hover:shadow-xl">
                      <Heart className="w-4 h-4 text-red-500" />
                    </button>
                    <button
                      onClick={() => navigate(`/products/${product._id}`)}
                      className="bg-white/90 hover:bg-white p-2 rounded-full shadow-lg transition-all hover:scale-110 hover:shadow-xl"
                    >
                      <Eye className="w-4 h-4 text-blue-500" />
                    </button>
                  </div>

                  {/* Stock Status */}
                  {!product.inStock && (
                    <div className="absolute bottom-3 left-3 bg-red-500 text-white text-xs font-bold px-3 py-1 rounded-full animate-pulse">
                      Out of Stock
                    </div>
                  )}

                  {/* Category Badge */}
                  <div className="absolute bottom-3 right-3">
                    <span className="px-3 py-1 bg-black/20 text-white text-xs font-medium rounded-full backdrop-blur-sm">
                      {product.category}
                    </span>
                  </div>
                </div>

                {/* Product Info */}
                <div className="p-6">
                  {/* Product Name */}
                  <h3 className="text-lg font-bold text-gray-800 mb-3 line-clamp-2 group-hover:text-green-600 transition-colors duration-300">
                    {product.name}
                  </h3>

                  {/* Description */}
                  <p className="text-gray-600 text-sm mb-4 line-clamp-2 leading-relaxed">
                    {product.description}
                  </p>

                  {/* Rating */}
                  <div className="flex items-center gap-2 mb-4">
                    <div className="flex items-center">
                      {[...Array(5)].map((_, i) => (
                        <Star
                          key={i}
                          className={`w-4 h-4 ${
                            i < Math.floor(product.rating)
                              ? "text-yellow-400 fill-current"
                              : "text-gray-300"
                          }`}
                        />
                      ))}
                    </div>
                    <span className="text-sm text-gray-600">
                      ({product.reviews})
                    </span>
                    <span className="ml-auto text-xs text-gray-500 bg-gray-100 px-2 py-1 rounded-full">
                      {product.rating}/5
                    </span>
                  </div>

                  {/* Price */}
                  <div className="flex items-center gap-3 mb-4">
                    <span className="text-2xl font-bold text-green-600">
                      â‚¹{product.price}
                    </span>
                    {product.originalPrice > product.price && (
                      <span className="text-lg text-gray-400 line-through">
                        â‚¹{product.originalPrice}
                      </span>
                    )}
                  </div>

                  {/* Supplier Info */}
                  <div className="flex items-center gap-2 mb-4 text-sm text-gray-500">
                    <Truck className="w-4 h-4 text-green-500" />
                    <span className="truncate">{product.supplier}</span>
                  </div>

                  {/* Cart Actions */}
                  <div className="space-y-3">
                    {isInCart(product._id) ? (
                      <div className="bg-green-50 border border-green-200 rounded-xl p-3">
                        <div className="flex items-center gap-2 mb-2">
                          <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                          <span className="text-sm font-medium text-green-800">
                            In Cart: {getItemQuantity(product._id)} Ã— â‚¹{product.price}
                          </span>
                        </div>
                        <div className="flex items-center gap-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() =>
                              handleQuantityChange(
                                product._id,
                                getItemQuantity(product._id) - 1
                              )
                            }
                            disabled={isItemUpdating(product._id)}
                            className="flex-1 border-green-300 text-green-700 hover:bg-green-100"
                          >
                            <Minus className="w-4 h-4" />
                          </Button>
                          <span className="text-center font-semibold min-w-[2rem]">
                            {getItemQuantity(product._id)}
                          </span>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() =>
                              handleQuantityChange(
                                product._id,
                                getItemQuantity(product._id) + 1
                              )
                            }
                            disabled={isItemUpdating(product._id)}
                            className="flex-1 border-green-300 text-green-700 hover:bg-green-100"
                          >
                            <Plus className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    ) : (
                      <Button
                        onClick={() => handleAddToCart(product)}
                        disabled={!product.inStock}
                        className="w-full bg-gradient-to-r from-green-600 to-blue-600 hover:from-green-700 hover:to-blue-700 text-white border-0 shadow-lg hover:shadow-xl transition-all duration-300 group-hover:scale-105"
                      >
                        <ShoppingCart className="w-4 h-4 mr-2" />
                        Add to Cart
                      </Button>
                    )}

                    {/* View Details Button */}
                    <Button
                      variant="outline"
                      onClick={() => navigate(`/products/${product._id}`)}
                      className="w-full border-green-200 text-green-600 hover:bg-green-50 hover:border-green-300 transition-all duration-300 group-hover:scale-105"
                    >
                      <Eye className="w-4 h-4 mr-2" />
                      View Details
                    </Button>
                  </div>
                </div>

                {/* Hover Effect Border */}
                <div className="absolute inset-0 border-2 border-transparent group-hover:border-green-300/50 rounded-xl transition-all duration-300 pointer-events-none"></div>
              </Card>
            ))}
          </div>
        )}

        {/* Bottom CTA */}
        {filteredProducts.length > 0 && (
          <div className="mt-20 text-center">
            <div className="relative overflow-hidden bg-gradient-to-r from-green-600 via-blue-600 to-purple-600 rounded-3xl p-12 text-white">
              {/* Background Elements */}
              <div className="absolute inset-0">
                <div className="absolute top-0 left-0 w-32 h-32 bg-white/10 rounded-full mix-blend-multiply filter blur-xl animate-blob"></div>
                <div className="absolute bottom-0 right-0 w-32 h-32 bg-white/10 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-2000"></div>
              </div>

              <div className="relative">
                <div className="flex items-center justify-center gap-2 mb-4">
                  <Sparkles className="w-6 h-6 text-yellow-300 animate-pulse" />
                  <span className="text-sm font-medium bg-white/20 px-4 py-2 rounded-full">
                    Expert Guidance
                  </span>
                  <Sparkles className="w-6 h-6 text-yellow-300 animate-pulse" />
                </div>

                <h3 className="text-3xl font-bold mb-6">
                  Need Help Finding the Right Products?
                </h3>
                <p className="text-green-100 mb-8 max-w-3xl mx-auto text-lg leading-relaxed">
                  Our nutrition experts are here to help you choose products that
                  align with your dietary goals and preferences. Get personalized
                  recommendations based on your health objectives.
                </p>

                <div className="flex flex-col sm:flex-row gap-4 justify-center">
                  <Button
                    variant="secondary"
                    size="lg"
                    className="bg-white text-green-600 hover:bg-gray-100 shadow-lg hover:shadow-xl transition-all duration-300"
                  >
                    <TrendingUp className="w-5 h-5 mr-2" />
                    Get Personalized Recommendations
                  </Button>
                  <Button
                    variant="outline"
                    size="lg"
                    className="border-white/30 text-white hover:bg-white/10 backdrop-blur-sm"
                  >
                    <Users className="w-5 h-5 mr-2" />
                    Chat with Nutritionist
                  </Button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Custom CSS for slider */}
      <style jsx>{`
        .slider::-webkit-slider-thumb {
          appearance: none;
          height: 20px;
          width: 20px;
          border-radius: 50%;
          background: linear-gradient(to right, #10b981, #3b82f6);
          cursor: pointer;
          box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
        }

        .slider::-moz-range-thumb {
          height: 20px;
          width: 20px;
          border-radius: 50%;
          background: linear-gradient(to right, #10b981, #3b82f6);
          cursor: pointer;
          border: none;
          box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
        }

        @keyframes blob {
          0% { transform: translate(0px, 0px) scale(1); }
          33% { transform: translate(30px, -50px) scale(1.1); }
          66% { transform: translate(-20px, 20px) scale(0.9); }
          100% { transform: translate(0px, 0px) scale(1); }
        }

        .animate-blob {
          animation: blob 7s infinite;
        }

        .animation-delay-2000 {
          animation-delay: 2s;
        }

        .animation-delay-4000 {
          animation-delay: 4s;
        }
      `}</style>
    </div>
  );
};

export default ProductsPage;
