import React, { createContext, useContext, useState, useEffect } from 'react';
import { useAuth } from './AuthContext';
import { cartAPI } from '../services/api';
import { toast } from '../components/ui/use-toast';

const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const { user, isAuthenticated } = useAuth();
  const [cart, setCart] = useState({ items: [], total: 0, count: 0 });
  const [loading, setLoading] = useState(false);
  const [updatingItems, setUpdatingItems] = useState(new Set()); // Track which items are being updated

  // Load cart from API when user is authenticated
  useEffect(() => {
    if (isAuthenticated && user?.id) {
      loadCart();
    } else {
      // Clear cart when user is not authenticated
      setCart({ items: [], total: 0, count: 0 });
    }
  }, [isAuthenticated, user?.id]);

  const loadCart = async () => {
    try {
      setLoading(true);
      const cartData = await cartAPI.getCart(user.id);
      setCart({
        ...cartData,
        count: cartData.items.reduce((sum, item) => sum + item.quantity, 0)
      });
    } catch (error) {
      console.error('Error loading cart:', error);
      toast({
        title: "Error",
        description: "Failed to load cart. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const addToCart = async (productId, quantity = 1) => {
    if (!isAuthenticated || !user?.id) {
      toast({
        title: "Please Sign In",
        description: "You need to sign in to add items to cart.",
        variant: "destructive",
      });
      return;
    }

    try {
      // Only set loading for this specific item
      setUpdatingItems(prev => new Set(prev).add(productId));

      const cartData = await cartAPI.addItem(user.id, productId, quantity);

      setCart({
        ...cartData,
        count: cartData.items.reduce((sum, item) => sum + item.quantity, 0)
      });

      toast({
        title: "Added to Cart",
        description: "Item has been added to your cart successfully!",
        variant: "default",
      });
    } catch (error) {
      console.error('Error adding to cart:', error);
      toast({
        title: "Error",
        description: error.message || "Failed to add item to cart. Please try again.",
        variant: "destructive",
      });
    } finally {
      setUpdatingItems(prev => {
        const newSet = new Set(prev);
        newSet.delete(productId);
        return newSet;
      });
    }
  };

  const updateQuantity = async (productId, quantity) => {
    if (!isAuthenticated || !user?.id) return;

    try {
      setUpdatingItems(prev => new Set(prev).add(productId));

      const cartData = await cartAPI.updateQuantity(user.id, productId, quantity);

      setCart({
        ...cartData,
        count: cartData.items.reduce((sum, item) => sum + item.quantity, 0)
      });

      if (quantity > 0) {
        toast({
          title: "Cart Updated",
          description: "Cart quantity has been updated successfully!",
          variant: "default",
        });
      }
    } catch (error) {
      console.error('Error updating quantity:', error);
      toast({
        title: "Error",
        description: error.message || "Failed to update quantity. Please try again.",
        variant: "destructive",
      });
    } finally {
      setUpdatingItems(prev => {
        const newSet = new Set(prev);
        newSet.delete(productId);
        return newSet;
      });
    }
  };

  const removeFromCart = async (productId) => {
    if (!isAuthenticated || !user?.id) return;

    try {
      setUpdatingItems(prev => new Set(prev).add(productId));

      const cartData = await cartAPI.removeItem(user.id, productId);

      setCart({
        ...cartData,
        count: cartData.items.reduce((sum, item) => sum + item.quantity, 0)
      });

      toast({
        title: "Removed from Cart",
        description: "Item has been removed from your cart.",
        variant: "default",
      });
    } catch (error) {
      console.error('Error removing from cart:', error);
      toast({
        title: "Error",
        description: error.message || "Failed to remove item from cart. Please try again.",
        variant: "destructive",
      });
    } finally {
      setUpdatingItems(prev => {
        const newSet = new Set(prev);
        newSet.delete(productId);
        return newSet;
      });
    }
  };

  const clearCart = async () => {
    if (!isAuthenticated || !user?.id) return;

    try {
      setLoading(true);
      await cartAPI.clearCart(user.id);
      setCart({ items: [], total: 0, count: 0 });

      toast({
        title: "Cart Cleared",
        description: "Your cart has been cleared successfully.",
        variant: "default",
      });
    } catch (error) {
      console.error('Error clearing cart:', error);
      toast({
        title: "Error",
        description: "Failed to clear cart. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const getItemQuantity = (productId) => {
    const item = cart.items.find(item => item.productId === productId);
    return item ? item.quantity : 0;
  };

  const isInCart = (productId) => {
    return cart.items.some(item => item.productId === productId);
  };

  const isItemUpdating = (productId) => {
    return updatingItems.has(productId);
  };

  const value = {
    cart, 
    loading, 
    addToCart, 
    updateQuantity, 
    removeFromCart, 
    clearCart, 
    getItemQuantity, 
    isInCart, 
    loadCart,
    isItemUpdating
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
