import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";
import { useCart } from "../../contexts/CartContext";
import { Button } from "../ui/button";
import { toast } from "../ui/use-toast";
import {
  Clock,
  Users,
  Flame,
  BarChart3,
  Plus,
  Minus,
  Trash2,
} from "lucide-react";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "./card";
import { Badge } from "./badge";

export function RecipeIntegration() {
  const navigate = useNavigate();
  const { addToCart, removeFromCart, updateQuantity, cart } = useCart();
  const { user, isAuthenticated } = useAuth();

  // Mock products database - in real app this would come from API
  const productsDatabase = {
    Quinoa: { name: "Quinoa", price: 120, available: true, category: "Grains" },
    "Sweet potato": {
      name: "Sweet potato",
      price: 40,
      available: true,
      category: "Vegetables",
    },
    Chickpeas: {
      name: "Chickpeas",
      price: 60,
      available: true,
      category: "Legumes",
    },
    Kale: { name: "Kale", price: 30, available: true, category: "Vegetables" },
    Avocado: {
      name: "Avocado",
      price: 80,
      available: true,
      category: "Fruits",
    },
    "Salmon fillet": {
      name: "Salmon fillet",
      price: 400,
      available: true,
      category: "Seafood",
    },
    "Olive oil": {
      name: "Olive oil",
      price: 200,
      available: true,
      category: "Oils",
    },
    Lemon: { name: "Lemon", price: 20, available: true, category: "Fruits" },
    "Fresh herbs": {
      name: "Fresh herbs",
      price: 50,
      available: true,
      category: "Herbs",
    },
    Tomato: {
      name: "Tomato",
      price: 25,
      available: true,
      category: "Vegetables",
    },
    Onion: {
      name: "Onion",
      price: 30,
      available: true,
      category: "Vegetables",
    },
    Garlic: {
      name: "Garlic",
      price: 40,
      available: true,
      category: "Vegetables",
    },
  };

  const [recipeIngredients, setRecipeIngredients] = useState({
    "Quinoa Buddha Bowl": [
      {
        name: "Quinoa",
        quantity: "1 cup",
        productName: "Quinoa",
        price: 120,
        inPantry: false,
        inCart: false,
        cartQuantity: 0,
        available: true,
      },
      {
        name: "Sweet potato",
        quantity: "1 medium",
        productName: "Sweet potato",
        price: 40,
        inPantry: false,
        inCart: false,
        cartQuantity: 0,
        available: true,
      },
      {
        name: "Chickpeas",
        quantity: "1 can",
        productName: "Chickpeas",
        price: 60,
        inPantry: false,
        inCart: false,
        cartQuantity: 0,
        available: true,
      },
      {
        name: "Kale",
        quantity: "2 cups",
        productName: "Kale",
        price: 30,
        inPantry: false,
        inCart: false,
        cartQuantity: 0,
        available: true,
      },
      {
        name: "Avocado",
        quantity: "1",
        productName: "Avocado",
        price: 80,
        inPantry: false,
        inCart: false,
        cartQuantity: 0,
        available: true,
      },
    ],
    "Mediterranean Salmon": [
      {
        name: "Salmon fillet",
        quantity: "2 pieces",
        productName: "Salmon fillet",
        price: 400,
        inPantry: false,
        inCart: false,
        cartQuantity: 0,
        available: true,
      },
      {
        name: "Olive oil",
        quantity: "2 tbsp",
        productName: "Olive oil",
        price: 200,
        inPantry: false,
        inCart: false,
        cartQuantity: 0,
        available: true,
      },
      {
        name: "Lemon",
        quantity: "1",
        productName: "Lemon",
        price: 20,
        inPantry: false,
        inCart: false,
        cartQuantity: 0,
        available: true,
      },
      {
        name: "Fresh herbs",
        quantity: "1 bunch",
        productName: "Fresh herbs",
        price: 50,
        inPantry: false,
        inCart: false,
        cartQuantity: 0,
        available: true,
      },
      {
        name: "Quinoa",
        quantity: "1 cup",
        productName: "Quinoa",
        price: 120,
        inPantry: false,
        inCart: false,
        cartQuantity: 0,
        available: true,
      },
    ],
  });

  // Debug logging - moved after state initialization
  console.log("RecipeIntegration: User:", user);
  console.log("RecipeIntegration: IsAuthenticated:", isAuthenticated);
  console.log("RecipeIntegration: Cart:", cart);
  console.log("RecipeIntegration: Recipe Ingredients:", recipeIngredients);

  // Test cart state update
  useEffect(() => {
    console.log("RecipeIntegration component re-rendered with cart:", cart);
  }, [cart]);

  // Update ingredient status based on global cart
  useEffect(() => {
    console.log("Global cart items changed, updating ingredient status:", cart.items);

    // Create a new object to trigger re-render
    const updatedIngredients = {};

    Object.keys(recipeIngredients).forEach((recipeName) => {
      updatedIngredients[recipeName] = recipeIngredients[recipeName].map(
        (ingredient) => {
          const cartItem = cart.items.find(
            (item) => item.productId === ingredient.productName
          );
          const isAvailable = ingredient.available;

          const updatedIngredient = {
            ...ingredient,
            inCart: !!cartItem,
            cartQuantity: cartItem ? cartItem.quantity : 0,
            available: isAvailable,
          };

          console.log(
            `Updating ${ingredient.name}: inCart=${updatedIngredient.inCart}, cartQuantity=${updatedIngredient.cartQuantity}`
          );

          return updatedIngredient;
        }
      );
    });

    console.log("Updated recipe ingredients:", updatedIngredients);
    setRecipeIngredients(updatedIngredients);
  }, [cart.items]); // Only depend on global cart changes

  const handleAddIngredientToCart = async (recipeName, ingredient) => {
    console.log(
      "Adding ingredient to cart:",
      ingredient.name,
      ingredient.productName
    );

    if (!ingredient.available) {
      toast({
        title: "Product Unavailable",
        description: `${ingredient.name} is currently not available in our store.`,
        variant: "destructive",
      });
      return;
    }

    try {
      // Add to global cart using product name as ID
      console.log("Calling addToCart with:", ingredient.productName, 1);
      await addToCart(ingredient.productName, 1);
      console.log("Successfully added to global cart");

      // Update local state immediately for better UX
      setRecipeIngredients((prev) => ({
        ...prev,
        [recipeName]: prev[recipeName].map((item) =>
          item.name === ingredient.name
            ? { ...item, inCart: true, cartQuantity: 1 }
            : item
        ),
      }));

      toast({
        title: "Added to Cart",
        description: `${ingredient.name} has been added to your cart!`,
        variant: "default",
      });
    } catch (error) {
      console.error("Error adding ingredient to cart:", error);
      toast({
        title: "Error",
        description: "Failed to add ingredient to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleRemoveIngredientFromCart = async (recipeName, ingredient) => {
    try {
      await removeFromCart(ingredient.productName);

      // Update local state immediately for better UX
      setRecipeIngredients((prev) => ({
        ...prev,
        [recipeName]: prev[recipeName].map((item) =>
          item.name === ingredient.name
            ? { ...item, inCart: false, cartQuantity: 0 }
            : item
        ),
      }));

      toast({
        title: "Removed from Cart",
        description: `${ingredient.name} has been removed from your cart!`,
        variant: "default",
      });
    } catch (error) {
      console.error("Error removing ingredient from cart:", error);
      toast({
        title: "Error",
        description: "Failed to remove ingredient from cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleUpdateIngredientQuantity = async (
    recipeName,
    ingredient,
    newQuantity
  ) => {
    if (newQuantity <= 0) {
      await handleRemoveIngredientFromCart(recipeName, ingredient);
      return;
    }

    try {
      await updateQuantity(ingredient.productName, newQuantity);

      // Update local state immediately for better UX
      setRecipeIngredients((prev) => ({
        ...prev,
        [recipeName]: prev[recipeName].map((item) =>
          item.name === ingredient.name
            ? { ...item, cartQuantity: newQuantity }
            : item
        ),
      }));

      toast({
        title: "Quantity Updated",
        description: `${ingredient.name} quantity updated to ${newQuantity}!`,
        variant: "default",
      });
    } catch (error) {
      console.error("Error updating ingredient quantity:", error);
      toast({
        title: "Error",
        description: "Failed to update quantity. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleOrderMissingIngredients = async (recipeName) => {
    try {
      const missingIngredients = recipeIngredients[recipeName].filter(
        (ingredient) => !ingredient.inCart && ingredient.available
      );

      if (missingIngredients.length === 0) {
        toast({
          title: "All Ingredients Available",
          description:
            "All ingredients for this recipe are already in your cart!",
          variant: "default",
        });
        return;
      }

      // Add all missing ingredients to global cart
      for (const ingredient of missingIngredients) {
        await addToCart(ingredient.productName, 1);
      }

      toast({
        title: "Ingredients Added",
        description: `${missingIngredients.length} ingredients have been added to your cart!`,
        variant: "default",
      });

      // Redirect to cart page to show the updated cart
      navigate("/cart");
    } catch (error) {
      console.error("Error ordering missing ingredients:", error);
      toast({
        title: "Error",
        description: "Failed to order missing ingredients. Please try again.",
        variant: "destructive",
      });
    }
  };

  const sampleRecipes = [
    {
      name: "Quinoa Buddha Bowl",
      difficulty: "Easy",
      time: "25 min",
      servings: 2,
      calories: 420,
      ingredients: recipeIngredients["Quinoa Buddha Bowl"],
      image: "🥗",
      tags: ["Vegan", "High Protein", "Budget Friendly"],
    },
    {
      name: "Mediterranean Salmon",
      difficulty: "Medium",
      time: "35 min",
      servings: 2,
      calories: 580,
      ingredients: recipeIngredients["Mediterranean Salmon"],
      image: "🐟",
      tags: ["High Protein", "Omega-3", "Premium"],
    },
  ];

  const renderIngredientStatus = (recipeName, ingredient) => {
    if (!ingredient.available) {
      return (
        <Badge variant="destructive" className="text-xs">
          Not Available
        </Badge>
      );
    }

    if (ingredient.inCart) {
      return (
        <div className="flex items-center space-x-2">
          <div className="flex items-center space-x-1">
            <button
              onClick={() =>
                handleUpdateIngredientQuantity(
                  recipeName,
                  ingredient,
                  ingredient.cartQuantity - 1
                )
              }
              className="w-6 h-6 rounded-full bg-red-100 text-red-600 hover:bg-red-200 flex items-center justify-center text-sm font-bold"
            >
              -
            </button>
            <span className="text-sm font-medium text-gray-700 min-w-[20px] text-center">
              {ingredient.cartQuantity}
            </span>
            <button
              onClick={() =>
                handleUpdateIngredientQuantity(
                  recipeName,
                  ingredient,
                  ingredient.cartQuantity + 1
                )
              }
              className="w-6 h-6 rounded-full bg-green-100 text-green-600 hover:bg-green-200 flex items-center justify-center text-sm font-bold"
            >
              +
            </button>
          </div>
          <Button
            variant="outline"
            size="sm"
            className="text-xs text-red-600 hover:text-red-700"
            onClick={() =>
              handleRemoveIngredientFromCart(recipeName, ingredient)
            }
          >
            Remove
          </Button>
        </div>
      );
    }

    return (
      <Button
        variant="outline"
        size="sm"
        className="text-xs"
        onClick={() => handleAddIngredientToCart(recipeName, ingredient)}
      >
        Add to Cart
      </Button>
    );
  };

  return (
    <section className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <Badge variant="info" className="mb-4 text-sm">
            👨‍🍳 Smart Cooking Mode
          </Badge>
          <h2 className="text-3xl font-bold text-foreground sm:text-4xl lg:text-5xl mb-6 font-heading">
            Recipe Integration +
            <span className="block text-transparent bg-clip-text bg-gradient-to-r from-primary to-secondary">
              Smart Ingredient Ordering
            </span>
          </h2>
          <p className="text-xl text-muted max-w-3xl mx-auto font-body">
            Each diet plan generates weekly recipe suggestions with step-by-step
            instructions. Order missing ingredients with one click and sync with
            your pantry.
          </p>
        </div>

        {/* Recipe Cards */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-16">
          {sampleRecipes.map((recipe, index) => (
            <Card
              key={index}
              className="group hover:shadow-xl transition-all duration-300 overflow-hidden"
            >
              <div className="h-2 bg-gradient-to-r from-primary to-secondary"></div>

              <CardHeader className="pb-4">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <CardTitle className="text-2xl font-bold text-foreground font-heading">
                      {recipe.name}
                    </CardTitle>
                    <CardDescription className="text-muted mt-2 font-body">
                      A delicious and nutritious meal perfect for your diet plan
                    </CardDescription>
                  </div>
                  <div className="text-4xl ml-4">{recipe.image}</div>
                </div>

                {/* Recipe Meta */}
                <div className="flex items-center space-x-4 mt-4">
                  <Badge variant="outline" className="text-xs">
                    ⏱️ {recipe.time}
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    👥 {recipe.servings} servings
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    🔥 {recipe.calories} cal
                  </Badge>
                  <Badge variant="outline" className="text-xs">
                    📊 {recipe.difficulty}
                  </Badge>
                </div>

                {/* Tags */}
                <div className="flex flex-wrap gap-2 mt-3">
                  {recipe.tags.map((tag, idx) => (
                    <Badge key={idx} variant="secondary" className="text-xs">
                      {tag}
                    </Badge>
                  ))}
                </div>
              </CardHeader>

              <CardContent className="space-y-6">
                {/* Ingredients List */}
                <div>
                  <h4 className="font-semibold text-foreground mb-3 font-heading">
                    Ingredients
                  </h4>
                  <div className="space-y-2">
                    {recipe.ingredients.map((ingredient, idx) => (
                      <div
                        key={idx}
                        className="flex items-center justify-between p-3 rounded-lg bg-gray-50"
                      >
                        <div className="flex items-center space-x-3">
                          <div
                            className={`w-3 h-3 rounded-full ${
                              ingredient.inCart
                                ? "bg-success"
                                : ingredient.available
                                ? "bg-warning"
                                : "bg-gray-400"
                            }`}
                          ></div>
                          <div>
                            <span className="text-sm text-foreground font-medium">
                              {ingredient.name}
                            </span>
                            <div className="text-xs text-muted">
                              {ingredient.quantity}
                              {ingredient.available && (
                                <span className="ml-2 text-green-600">
                                  • ₹
                                  {
                                    productsDatabase[ingredient.productName]
                                      ?.price
                                  }
                                </span>
                              )}
                            </div>
                          </div>
                        </div>
                        {renderIngredientStatus(recipe.name, ingredient)}
                      </div>
                    ))}
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-3">
                  <Button
                    variant="outline"
                    className="flex-1"
                    size="sm"
                    onClick={() =>
                      navigate(`/recipes/${encodeURIComponent(recipe.name)}`)
                    }
                  >
                    View Full Recipe
                  </Button>
                  <Button
                    variant="gradient"
                    className="flex-1"
                    size="sm"
                    onClick={() => handleOrderMissingIngredients(recipe.name)}
                    disabled={recipe.ingredients.every(
                      (ingredient) => ingredient.inCart || !ingredient.available
                    )}
                  >
                    Order Missing Ingredients
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Smart Features Showcase */}
        <div className="bg-gradient-to-r from-primary/5 to-secondary/5 rounded-3xl p-12">
          <div className="text-center mb-12">
            <h3 className="text-2xl font-bold text-foreground mb-4 font-heading">
              🚀 Smart Cooking Features
            </h3>
            <p className="text-muted max-w-2xl mx-auto font-body">
              Our smart cooking assistant makes meal preparation effortless and
              cost-effective
            </p>
          </div>

          {/* Debug Test Button */}
          <div className="text-center mb-8">
            <Button
              variant="outline"
              onClick={() => {
                console.log("Current cart state:", cart);
                console.log("Current recipe ingredients:", recipeIngredients);
                // Test adding an ingredient
                const testIngredient =
                  recipeIngredients["Quinoa Buddha Bowl"][0];
                handleAddIngredientToCart("Quinoa Buddha Bowl", testIngredient);
              }}
            >
              🧪 Test Cart Function
            </Button>

            {/* Debug Cart Display */}
            <div className="mt-4 p-4 bg-gray-100 rounded-lg text-left">
              <h4 className="font-semibold mb-2">Debug Cart State:</h4>
              <p>Cart Items: {cart.items.length}</p>
              <p>Cart Count: {cart.count}</p>
              <p>Cart Total: ₹{cart.total}</p>
              <div className="text-sm">
                {cart.items.map((item, idx) => (
                  <div key={idx}>
                    {item.productId}: {item.quantity} (₹{item.price})
                  </div>
                ))}
              </div>
              <div className="mt-2 text-xs text-gray-600">
                <p>Last Updated: {new Date().toLocaleTimeString()}</p>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center">
              <div className="w-20 h-20 bg-primary/10 rounded-full flex items-center mx-auto mb-4">
                <span className="text-3xl">📱</span>
              </div>
              <h4 className="font-semibold text-foreground mb-3 font-heading">
                One-Click Ordering
              </h4>
              <p className="text-sm text-muted font-body">
                Order missing ingredients for any recipe with a single tap. We
                automatically add only what you need.
              </p>
            </div>

            <div className="text-center">
              <div className="w-20 h-20 bg-secondary/10 rounded-full flex items-center mx-auto mb-4">
                <span className="text-3xl">🏠</span>
              </div>
              <h4 className="font-semibold text-foreground mb-3 font-heading">
                Pantry Sync
              </h4>
              <p className="text-sm text-muted font-body">
                Upload photos of your pantry or input manually. We suggest
                recipes using what you already have.
              </p>
            </div>

            <div className="text-center">
              <div className="w-20 h-20 bg-accent/10 rounded-full flex items-center mx-auto mb-4">
                <span className="text-3xl">🎯</span>
              </div>
              <h4 className="font-semibold text-foreground mb-3 font-heading">
                Diet Optimization
              </h4>
              <p className="text-sm text-muted font-body">
                Recipes automatically adjust to your dietary restrictions,
                allergies, and health goals.
              </p>
            </div>
          </div>

          {/* CTA Section */}
          <div className="text-center mt-12">
            <div className="bg-white rounded-2xl p-8 shadow-lg border border-gray-200 max-w-2xl mx-auto">
              <h4 className="text-xl font-bold text-foreground mb-4 font-heading">
                Ready to Start Cooking Smart?
              </h4>
              <p className="text-muted mb-6 font-body">
                Get personalized recipes, smart ingredient ordering, and save
                money on groceries
              </p>
              <div className="flex justify-center">
                <Button
                  variant="gradient"
                  size="lg"
                  onClick={() => navigate("/recipes")}
                >
                  View Free Recipes
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
