import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "./card";
import { Button } from "./button";
import { Badge } from "./badge";
import { Input } from "./input";

export function DietCompatibilityChecker() {
  const navigate = useNavigate();
  const [selectedDiet, setSelectedDiet] = useState("");
  const [dietaryRestrictions, setDietaryRestrictions] = useState([]);
  const [allergies, setAllergies] = useState([]);
  const [budget, setBudget] = useState("");

  const dietTypes = [
    { id: "keto", name: "Keto", icon: "ü•ë", description: "High-fat, low-carb" },
    { id: "vegan", name: "Vegan", icon: "üå±", description: "Plant-based only" },
    {
      id: "vegetarian",
      name: "Vegetarian",
      icon: "ü•¨",
      description: "No meat, includes dairy",
    },
    {
      id: "paleo",
      name: "Paleo",
      icon: "ü•©",
      description: "Ancient human diet",
    },
    {
      id: "mediterranean",
      name: "Mediterranean",
      icon: "üêü",
      description: "Heart-healthy",
    },
    {
      id: "intermittent",
      name: "Intermittent Fasting",
      icon: "‚è∞",
      description: "Time-restricted eating",
    },
  ];

  const restrictionOptions = [
    "Lactose Intolerant",
    "Gluten Free",
    "Dairy Free",
    "Nut Free",
    "Soy Free",
    "Egg Free",
  ];

  const allergyOptions = [
    "Peanuts",
    "Tree Nuts",
    "Milk",
    "Eggs",
    "Soy",
    "Wheat",
    "Fish",
    "Shellfish",
  ];

  const budgetOptions = [
    {
      value: "2000",
      label: "‚Çπ2,000/month",
      description: "Budget-friendly options",
    },
    { value: "5000", label: "‚Çπ5,000/month", description: "Balanced variety" },
    { value: "8000", label: "‚Çπ8,000/month", description: "Premium organic" },
    { value: "12000", label: "‚Çπ12,000/month", description: "Luxury gourmet" },
  ];

  const toggleRestriction = (restriction) => {
    setDietaryRestrictions((prev) =>
      prev.includes(restriction)
        ? prev.filter((r) => r !== restriction)
        : [...prev, restriction]
    );
  };

  const toggleAllergy = (allergy) => {
    setAllergies((prev) =>
      prev.includes(allergy)
        ? prev.filter((a) => a !== allergy)
        : [...prev, allergy]
    );
  };

  const calculateCompatibility = () => {
    let score = 100;

    // Reduce score based on restrictions
    score -= dietaryRestrictions.length * 5;
    score -= allergies.length * 8;

    // Ensure minimum score
    return Math.max(score, 20);
  };

  const getRecommendedDiets = () => {
    if (!selectedDiet) return [];

    const recommendations = [];

    if (
      selectedDiet === "vegan" &&
      dietaryRestrictions.includes("Dairy Free")
    ) {
      recommendations.push(
        "Perfect match! Vegan diet naturally excludes dairy."
      );
    } else if (
      selectedDiet === "keto" &&
      dietaryRestrictions.includes("Gluten Free")
    ) {
      recommendations.push("Great choice! Keto is naturally gluten-free.");
    } else if (
      selectedDiet === "mediterranean" &&
      !dietaryRestrictions.includes("Fish")
    ) {
      recommendations.push(
        "Excellent! Mediterranean diet includes fish and healthy fats."
      );
    }

    return recommendations.length > 0
      ? recommendations
      : ["Your selected diet plan is compatible with your preferences."];
  };

  const getFilteredGroceries = () => {
    const baseGroceries = [
      "Fresh vegetables",
      "Lean proteins",
      "Whole grains",
      "Healthy fats",
      "Fruits",
    ];

    let filtered = [...baseGroceries];

    if (dietaryRestrictions.includes("Gluten Free")) {
      filtered = filtered.filter((item) => !["Whole grains"].includes(item));
      filtered.push("Quinoa", "Rice", "Gluten-free oats");
    }

    if (dietaryRestrictions.includes("Dairy Free")) {
      filtered = filtered.filter(
        (item) => !["Milk", "Cheese", "Yogurt"].includes(item)
      );
      filtered.push("Almond milk", "Coconut yogurt", "Nutritional yeast");
    }

    return filtered;
  };

  return (
    <section className="py-20 bg-gradient-to-br from-blue-50 to-indigo-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <Badge variant="info" className="mb-4 text-sm">
            üîç Diet Compatibility Checker
          </Badge>
          <h2 className="text-3xl font-bold text-foreground sm:text-4xl lg:text-5xl mb-6 font-heading">
            Find Your Perfect
            <span className="block text-transparent bg-clip-text bg-gradient-to-r from-primary to-secondary">
              Diet Match
            </span>
          </h2>
          <p className="text-xl text-muted max-w-3xl mx-auto font-body">
            Our AI-powered compatibility checker ensures your diet plan and
            grocery recommendations perfectly align with your dietary
            restrictions, allergies, and budget preferences.
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          {/* Input Section */}
          <div className="space-y-8">
            {/* Diet Type Selection */}
            <Card className="border-0 shadow-lg">
              <CardHeader>
                <CardTitle className="text-xl font-heading">
                  1. Choose Your Diet Type
                </CardTitle>
                <CardDescription className="font-body">
                  Select the diet plan you're interested in following
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 gap-3">
                  {dietTypes.map((diet) => (
                    <button
                      key={diet.id}
                      onClick={() => setSelectedDiet(diet.id)}
                      className={`p-4 rounded-xl border-2 transition-all duration-200 text-left ${
                        selectedDiet === diet.id
                          ? "border-primary bg-primary/5 shadow-md"
                          : "border-gray-200 hover:border-primary/50 hover:bg-gray-50"
                      }`}
                    >
                      <div className="text-2xl mb-2">{diet.icon}</div>
                      <div className="font-semibold text-foreground font-heading">
                        {diet.name}
                      </div>
                      <div className="text-sm text-muted font-body">
                        {diet.description}
                      </div>
                    </button>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Dietary Restrictions */}
            <Card className="border-0 shadow-lg">
              <CardHeader>
                <CardTitle className="text-xl font-heading">
                  2. Dietary Restrictions
                </CardTitle>
                <CardDescription className="font-body">
                  Select any dietary restrictions you have
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 gap-2">
                  {restrictionOptions.map((restriction) => (
                    <button
                      key={restriction}
                      onClick={() => toggleRestriction(restriction)}
                      className={`p-3 rounded-lg border transition-all duration-200 text-sm ${
                        dietaryRestrictions.includes(restriction)
                          ? "border-primary bg-primary/10 text-primary"
                          : "border-gray-200 hover:border-primary/50"
                      }`}
                    >
                      {restriction}
                    </button>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Allergies */}
            <Card className="border-0 shadow-lg">
              <CardHeader>
                <CardTitle className="text-xl font-heading">
                  3. Food Allergies
                </CardTitle>
                <CardDescription className="font-body">
                  Select any food allergies (this will be strictly avoided)
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 gap-2">
                  {allergyOptions.map((allergy) => (
                    <button
                      key={allergy}
                      onClick={() => toggleAllergy(allergy)}
                      className={`p-3 rounded-lg border transition-all duration-200 text-sm ${
                        allergies.includes(allergy)
                          ? "border-destructive bg-destructive/10 text-destructive"
                          : "border-gray-200 hover:border-destructive/50"
                      }`}
                    >
                      {allergy}
                    </button>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Budget Selection */}
            <Card className="border-0 shadow-lg">
              <CardHeader>
                <CardTitle className="text-xl font-heading">
                  4. Monthly Grocery Budget
                </CardTitle>
                <CardDescription className="font-body">
                  Choose your budget range for optimal recommendations
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {budgetOptions.map((option) => (
                    <button
                      key={option.value}
                      onClick={() => setBudget(option.value)}
                      className={`w-full p-4 rounded-lg border-2 transition-all duration-200 text-left ${
                        budget === option.value
                          ? "border-secondary bg-secondary/5 shadow-md"
                          : "border-gray-200 hover:border-secondary/50 hover:bg-gray-50"
                      }`}
                    >
                      <div className="font-semibold text-foreground font-heading">
                        {option.label}
                      </div>
                      <div className="text-sm text-muted font-body">
                        {option.description}
                      </div>
                    </button>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Results Section */}
          <div className="space-y-8">
            {/* Compatibility Score */}
            <Card className="border-0 shadow-lg bg-gradient-to-br from-primary/5 to-secondary/5">
              <CardHeader className="text-center">
                <CardTitle className="text-2xl font-heading">
                  Compatibility Score
                </CardTitle>
                <CardDescription className="font-body">
                  How well your preferences align with available options
                </CardDescription>
              </CardHeader>
              <CardContent className="text-center">
                <div className="text-6xl font-bold text-primary mb-4">
                  {calculateCompatibility()}%
                </div>
                <div className="w-full bg-gray-200 rounded-full h-3 mb-4">
                  <div
                    className="bg-gradient-to-r from-primary to-secondary h-3 rounded-full transition-all duration-1000 animate-shimmer"
                    style={{
                      width: `${calculateCompatibility()}%`,
                      backgroundImage:
                        "linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent)",
                    }}
                  ></div>
                </div>
                <p className="text-muted font-body">
                  {calculateCompatibility() >= 80
                    ? "Excellent compatibility!"
                    : calculateCompatibility() >= 60
                    ? "Good compatibility"
                    : "Limited options available"}
                </p>
              </CardContent>
            </Card>

            {/* Recommended Diet Plans */}
            <Card className="border-0 shadow-lg">
              <CardHeader>
                <CardTitle className="text-xl font-heading">
                  Recommended Diet Plans
                </CardTitle>
                <CardDescription className="font-body">
                  Based on your preferences and restrictions
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {getRecommendedDiets().map((recommendation, index) => (
                    <div
                      key={index}
                      className="flex items-start space-x-3 p-3 bg-green-50 rounded-lg"
                    >
                      <div className="w-2 h-2 bg-success rounded-full mt-2 flex-shrink-0"></div>
                      <p className="text-sm text-foreground font-body">
                        {recommendation}
                      </p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Filtered Grocery Recommendations */}
            <Card className="border-0 shadow-lg">
              <CardHeader>
                <CardTitle className="text-xl font-heading">
                  Filtered Grocery Recommendations
                </CardTitle>
                <CardDescription className="font-body">
                  Items that match your dietary requirements
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 gap-2">
                  {getFilteredGroceries().map((item, index) => (
                    <div
                      key={index}
                      className="flex items-center space-x-2 p-2 bg-blue-50 rounded-lg"
                    >
                      <div className="w-1.5 h-1.5 bg-info rounded-full"></div>
                      <span className="text-sm text-foreground font-body">
                        {item}
                      </span>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* How It Saves Time */}
            <Card className="border-0 shadow-lg bg-gradient-to-br from-accent/5 to-yellow-100">
              <CardHeader>
                <CardTitle className="text-lg font-heading">
                  ‚è∞ How Compatibility Checking Saves You Time
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3 text-sm">
                  <div className="flex items-center space-x-2">
                    <div className="w-1.5 h-1.5 bg-accent rounded-full"></div>
                    <span className="text-muted font-body">
                      No more reading ingredient labels
                    </span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <div className="w-1.5 h-1.5 bg-accent rounded-full"></div>
                    <span className="text-muted font-body">
                      Automatically filtered shopping lists
                    </span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <div className="w-1.5 h-1.5 bg-accent rounded-full"></div>
                    <span className="text-muted font-body">
                      Diet plans that actually work for you
                    </span>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>

        {/* CTA Section */}
        <div className="text-center mt-16">
          <div className="bg-gradient-to-r from-primary to-secondary rounded-3xl p-12 text-white">
            <h3 className="text-2xl font-bold mb-4 font-heading">
              Ready to Get Your Personalized Diet Plan?
            </h3>
            <p className="text-primary-foreground mb-8 max-w-2xl mx-auto font-body">
              Our AI will create a custom diet plan and grocery list that
              perfectly matches your preferences, restrictions, and budget
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Button
                variant="secondary"
                size="lg"
                className="bg-white text-primary hover:bg-gray-100"
                onClick={() => navigate("/signup")}
              >
                Generate My Diet Plan
              </Button>
              <Button
                variant="outline"
                size="lg"
                className="border-white text-white hover:bg-white hover:text-primary"
                onClick={() => navigate("/diet-plans")}
              >
                Learn More
              </Button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
