    const cart = await cartAPI.getCart(userId);
    const orders = await ordersAPI.getUserOrders(userId);
    const progress = await progressAPI.getUserProgress(userId);
    
    return {
      user,
      cart,
      recentOrders: orders.slice(0, 3),
      progress,
      recommendations: {
        products: dbHelpers.getAllProducts().slice(0, 4),
        recipes: dbHelpers.getAllRecipes().slice(0, 3)
      }
    };
  }
};

// Export all APIs
export default {
  auth: authAPI,
  dietPlans: dietPlansAPI,
  products: productsAPI,
  cart: cartAPI,
  orders: ordersAPI,
  recipes: recipesAPI,
  partnerships: partnershipsAPI,
  progress: progressAPI,
  dashboard: dashboardAPI
};
