import React, { useState, useEffect } from "react";
import { useAuth } from "../../contexts/AuthContext";
import { useCart } from "../../contexts/CartContext";

const RecipeIntegration = () => {
  const { user, isAuthenticated } = useAuth();
  const { cart, addToCart, removeFromCart, updateQuantity } = useCart();

  // State to store product details for cart items
  const [cartProductDetails, setCartProductDetails] = useState({});

  // Local state for recipe ingredients
  const [recipeIngredients, setRecipeIngredients] = useState([
    {
      id: 1,
      name: "Quinoa Buddha Bowl",
      description: "A delicious and nutritious meal perfect for your diet plan.",
      time: "25 min",
      servings: "2 servings",
      calories: "420 cal",
      difficulty: "Easy",
      tags: ["Vegan", "High Protein", "Budget Friendly"],
      ingredients: [
        { productName: "Quinoa", name: "Quinoa", quantity: "1 cup", price: 120 },
        { productName: "Sweet potato", name: "Sweet potato", quantity: "1 medium", price: 40 },
        { productName: "Chickpeas", name: "Chickpeas", quantity: "1 cup", price: 60 },
        { productName: "Kale", name: "Kale", quantity: "2 cups", price: 30 },
        { productName: "Avocado", name: "Avocado", quantity: "1", price: 80 },
      ],
    },
    {
      id: 2,
      name: "Mediterranean Salmon",
      description: "A delicious and nutritious meal perfect for your diet plan.",
      time: "35 min",
      servings: "2 servings",
      calories: "580 cal",
      difficulty: "Medium",
      tags: ["High Protein", "Omega-3", "Premium"],
      ingredients: [
        { productName: "Salmon fillet", name: "Salmon fillet", quantity: "2 pieces", price: 400 },
        { productName: "Olive oil", name: "Olive oil", quantity: "2 tbsp", price: 50 },
        { productName: "Lemon", name: "Lemon", quantity: "1", price: 20 },
        { productName: "Fresh herbs", name: "Fresh herbs", quantity: "1/4 cup", price: 30 },
        { productName: "Quinoa", name: "Quinoa", quantity: "1 cup", price: 120 },
      ],
    },
  ]);

  // Fetch product details for cart items
  useEffect(() => {
    const fetchCartProductDetails = async () => {
      if (!cart || !cart.items || cart.items.length === 0) return;

      const productDetails = {};

      for (const item of cart.items) {
        try {
          // Fetch product details by ID
          const response = await fetch(`http://localhost:4000/api/products/${item.productId}`);
          if (response.ok) {
            const product = await response.json();
            productDetails[product.name.toLowerCase()] = {
              productId: item.productId,
              quantity: item.quantity,
              price: item.price || product.price
            };
          }
        } catch (error) {
          console.error("Error fetching product details:", error);
        }
      }

      setCartProductDetails(productDetails);
    };

    fetchCartProductDetails();
  }, [cart]);

  // Check if an ingredient is in the global cart
  const isIngredientInCart = (ingredientName) => {
    return cartProductDetails[ingredientName.toLowerCase()] !== undefined;
  };

  // Get quantity of ingredient in global cart
  const getIngredientCartQuantity = (ingredientName) => {
    const cartItem = cartProductDetails[ingredientName.toLowerCase()];
    return cartItem ? cartItem.quantity : 0;
  };

  // Get product ID for ingredient
  const getIngredientProductId = (ingredientName) => {
    const cartItem = cartProductDetails[ingredientName.toLowerCase()];
    return cartItem ? cartItem.productId : null;
  };

  // Handle adding ingredient to cart
  const handleAddIngredientToCart = async (ingredient) => {
    try {
      // First, get the product ID from the database
      const response = await fetch(
        `http://localhost:4000/api/products/search?name=${encodeURIComponent(
          ingredient.productName
        )}`
      );
      if (response.ok) {
        const product = await response.json();
        // Now add to cart using the real product ID
        await addToCart(product._id, 1);
      } else {
        console.error("Product not found:", ingredient.productName);
      }
    } catch (error) {
      console.error("Error adding to cart:", error);
    }
  };

  // Handle removing ingredient from cart
  const handleRemoveIngredientFromCart = async (ingredient) => {
    try {
      // First, get the product ID from the database
      const response = await fetch(
        `http://localhost:4000/api/products/search?name=${encodeURIComponent(
          ingredient.productName
        )}`
      );
      if (response.ok) {
        const product = await response.json();
        // Now remove from cart using the real product ID
        await removeFromCart(product._id);
      } else {
        console.error("Product not found:", ingredient.productName);
      }
    } catch (error) {
      console.error("Error removing from cart:", error);
    }
  };

  // Handle updating ingredient quantity in cart
  const handleUpdateIngredientQuantity = async (ingredient, newQuantity) => {
    try {
      // First, get the product ID from the database
      const response = await fetch(
        `http://localhost:4000/api/products/search?name=${encodeURIComponent(
          ingredient.productName
        )}`
      );
      if (response.ok) {
        const product = await response.json();
        // Now update cart using the real product ID
        await updateQuantity(product._id, newQuantity);
      } else {
        console.error("Product not found:", ingredient.productName);
      }
    } catch (error) {
      console.error("Error updating cart:", error);
    }
  };

  // Handle ordering missing ingredients
  const handleOrderMissingIngredients = async (recipe) => {
    for (const ingredient of recipe.ingredients) {
      if (!isIngredientInCart(ingredient.productName)) {
        await handleAddIngredientToCart(ingredient);
      }
    }
  };

  // Render ingredient with proper cart state
  const renderIngredient = (ingredient, recipeId) => {
    const inCart = isIngredientInCart(ingredient.productName);
    const cartQuantity = getIngredientCartQuantity(ingredient.productName);

    return (
      <div key={ingredient.productName} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg mb-2">
        <div className="flex items-center space-x-3">
          <div className="w-2 h-2 bg-yellow-400 rounded-full"></div>
          <span className="font-medium">{ingredient.name}</span>
          <span className="text-gray-600">{ingredient.quantity}</span>
          <span className="text-green-600 font-semibold">₹{ingredient.price}</span>
        </div>

        {inCart ? (
          <div className="flex items-center space-x-2">
            <span className="text-sm text-green-600 font-medium">
              In Cart: {cartQuantity} × ₹{ingredient.price}
            </span>
            <div className="flex items-center space-x-1 bg-green-100 rounded-lg px-2 py-1">
              <button
                onClick={() => handleUpdateIngredientQuantity(ingredient, Math.max(0, cartQuantity - 1))}
                className="w-6 h-6 flex items-center justify-center text-green-600 hover:bg-green-200 rounded"
              >
                -
              </button>
              <span className="w-8 text-center font-medium">{cartQuantity}</span>
              <button
                onClick={() => handleUpdateIngredientQuantity(ingredient, cartQuantity + 1)}
                className="w-6 h-6 flex items-center justify-center text-green-600 hover:bg-green-200 rounded"
              >
                +
              </button>
            </div>
            <button
              onClick={() => handleRemoveIngredientFromCart(ingredient)}
              className="text-red-500 hover:text-red-700 text-sm font-medium"
            >
              Remove
            </button>
          </div>
        ) : (
          <button
            onClick={() => handleAddIngredientToCart(ingredient)}
            className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-colors"
          >
            ADD TO CART
          </button>
        )}
      </div>
    );
  };

  return (
    <section className="py-16 bg-gradient-to-br from-green-50 to-blue-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-12">
          <h2 className="text-4xl font-bold text-gray-900 mb-4">
            Recipe Integration + Smart Ingredient Ordering
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Discover delicious recipes and automatically add missing ingredients to your cart. 
            Our smart system checks what you already have and suggests what you need.
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {recipeIngredients.map((recipe) => (
            <div key={recipe.id} className="bg-white rounded-2xl shadow-lg p-6 hover:shadow-xl transition-shadow">
              <div className="mb-4">
                <h3 className="text-2xl font-bold text-gray-900 mb-2">{recipe.name}</h3>
                <p className="text-gray-600 mb-4">{recipe.description}</p>

                <div className="flex flex-wrap gap-2 mb-4">
                  <span className="flex items-center space-x-1 bg-gray-100 px-3 py-1 rounded-full text-sm">
                    <span>⏰</span>
                    <span>{recipe.time}</span>
                  </span>
                  <span className="flex items-center space-x-1 bg-gray-100 px-3 py-1 rounded-full text-sm">
                    <span>👥</span>
                    <span>{recipe.servings}</span>
                  </span>
                  <span className="flex items-center space-x-1 bg-gray-100 px-3 py-1 rounded-full text-sm">
                    <span>🔥</span>
                    <span>{recipe.calories}</span>
                  </span>
                  <span className="flex items-center space-x-1 bg-gray-100 px-3 py-1 rounded-full text-sm">
                    <span>📊</span>
                    <span>{recipe.difficulty}</span>
                  </span>
                </div>

                <div className="flex flex-wrap gap-2 mb-6">
                  {recipe.tags.map((tag, index) => (
                    <span
                      key={index}
                      className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm font-medium"
                    >
                      {tag}
                    </span>
                  ))}
                </div>

                <div className="mb-6">
                  <h4 className="text-lg font-semibold text-gray-900 mb-3">Ingredients</h4>
                  {recipe.ingredients.map((ingredient) => renderIngredient(ingredient, recipe.id))}
                </div>

                <div className="flex space-x-4">
                  <button className="flex-1 border-2 border-green-500 text-green-500 px-6 py-3 rounded-lg hover:bg-green-500 hover:text-white transition-colors font-medium">
                    VIEW FULL RECIPE
                  </button>
                  <button
                    onClick={() => handleOrderMissingIngredients(recipe)}
                    className="flex-1 bg-green-500 text-white px-6 py-3 rounded-lg hover:bg-green-600 transition-colors font-medium"
                  >
                    ORDER MISSING INGREDIENTS
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default RecipeIntegration;
