import React, { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";
import { useCart } from "../contexts/CartContext";
import { useNavigate } from "react-router-dom";

const RecipesPage = () => {
  const { user, isAuthenticated } = useAuth();
  const { cart, addToCart, removeFromCart, updateQuantity } = useCart();
  const navigate = useNavigate();
  
  // State for recipes and products
  const [recipes, setRecipes] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState("all");

  // Fetch recipes and products from database
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        
        // Fetch recipes from database
        const recipesResponse = await fetch("http://localhost:4000/api/recipes");
        const recipesData = await recipesResponse.json();
        
        // Fetch products from database
        const productsResponse = await fetch("http://localhost:4000/api/products");
        const productsData = await productsResponse.json();
        
        setRecipes(recipesData);
        setProducts(productsData);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Check if an ingredient is in the global cart
  const isIngredientInCart = (ingredientName) => {
    if (!cart || !cart.items || !products.length || !ingredientName) return false;
    
    const matchingProduct = products.find(
      (product) => product.name.toLowerCase() === ingredientName.toLowerCase()
    );
    
    if (!matchingProduct) return false;
    
    return cart.items.some((item) => item.productId === matchingProduct._id);
  };

  // Get quantity of ingredient in global cart
  const getIngredientCartQuantity = (ingredientName) => {
    if (!cart || !cart.items || !products.length || !ingredientName) return 0;
    
    const matchingProduct = products.find(
      (product) => product.name.toLowerCase() === ingredientName.toLowerCase()
    );
    
    if (!matchingProduct) return 0;
    
    const cartItem = cart.items.find(
      (item) => item.productId === matchingProduct._id
    );
    return cartItem ? cartItem.quantity : 0;
  };

  // Handle adding ingredient to cart
  const handleAddIngredientToCart = async (ingredientName) => {
    if (!ingredientName) return;
    
    try {
      const matchingProduct = products.find(
        (product) => product.name.toLowerCase() === ingredientName.toLowerCase()
      );
      
      if (matchingProduct) {
        await addToCart(matchingProduct._id, 1);
      } else {
        console.error("Product not found:", ingredientName);
      }
    } catch (error) {
      console.error("Error adding to cart:", error);
    }
  };

  // Handle removing ingredient from cart
  const handleRemoveIngredientFromCart = async (ingredientName) => {
    if (!ingredientName) return;
    
    try {
      const matchingProduct = products.find(
        (product) => product.name.toLowerCase() === ingredientName.toLowerCase()
      );
      
      if (matchingProduct) {
        await removeFromCart(matchingProduct._id);
      } else {
        console.error("Product not found:", ingredientName);
      }
    } catch (error) {
      console.error("Error removing from cart:", error);
    }
  };

  // Handle updating ingredient quantity in cart
  const handleUpdateIngredientQuantity = async (ingredientName, newQuantity) => {
    if (!ingredientName) return;
    
    try {
      const matchingProduct = products.find(
        (product) => product.name.toLowerCase() === ingredientName.toLowerCase()
      );
      
      if (matchingProduct) {
        await updateQuantity(matchingProduct._id, newQuantity);
      } else {
        console.error("Product not found:", ingredientName);
      }
    } catch (error) {
      console.error("Error updating cart:", error);
    }
  };

  // Handle ordering missing ingredients
  const handleOrderMissingIngredients = async (recipe) => {
    if (!recipe.ingredients) return;
    
    for (const ingredient of recipe.ingredients) {
      if (
        ingredient.productName &&
        !isIngredientInCart(ingredient.productName)
      ) {
        await handleAddIngredientToCart(ingredient.productName);
      }
    }
    
    // Redirect to cart after ordering ingredients
    navigate('/cart');
  };

  // Navigate to specific recipe page
  const handleLearnMore = (recipeId) => {
    navigate(`/recipes/${recipeId}`);
  };

  // Get unique categories from recipes
  const categories = ["all", ...new Set(recipes.map(recipe => recipe.difficulty).filter(Boolean))];

  // Filter recipes by category
  const filteredRecipes = selectedCategory === "all" 
    ? recipes 
    : recipes.filter(recipe => recipe.difficulty === selectedCategory);

  // Render ingredient with full cart functionality
  const renderIngredient = (ingredient) => {
    if (!ingredient) return null;
    
    const ingredientName = ingredient.productName || ingredient.name;
    if (!ingredientName) return null;
    
    const inCart = isIngredientInCart(ingredientName);
    const cartQuantity = getIngredientCartQuantity(ingredientName);
    
    const matchingProduct = products.find(
      (product) => product.name.toLowerCase() === ingredientName.toLowerCase()
    );
    
    const price = matchingProduct ? matchingProduct.price : ingredient.price || 0;

    return (
      <div
        key={ingredientName}
        className="flex items-center justify-between p-3 bg-gray-50 rounded-lg mb-2"
      >
        <div className="flex items-center space-x-3">
          <div className="w-2 h-2 bg-yellow-400 rounded-full"></div>
          <span className="font-medium">
            {ingredient.name || ingredientName}
          </span>
          <span className="text-gray-600">{ingredient.quantity}</span>
          <span className="text-green-600 font-semibold">‚Çπ{price}</span>
        </div>
        
        {inCart ? (
          <div className="flex items-center space-x-2">
            <span className="text-sm text-green-600 font-medium">
              In Cart: {cartQuantity} √ó ‚Çπ{price}
            </span>
            <div className="flex items-center space-x-1 bg-green-100 rounded-lg px-2 py-1">
              <button
                onClick={() =>
                  handleUpdateIngredientQuantity(
                    ingredientName,
                    Math.max(0, cartQuantity - 1)
                  )
                }
                className="w-6 h-6 flex items-center justify-center text-green-600 hover:bg-green-200 rounded"
              >
                -
              </button>
              <span className="w-8 text-center font-medium">
                {cartQuantity}
              </span>
              <button
                onClick={() =>
                  handleUpdateIngredientQuantity(
                    ingredientName,
                    cartQuantity + 1
                  )
                }
                className="w-6 h-6 flex items-center justify-center text-green-600 hover:bg-green-200 rounded"
              >
                +
              </button>
            </div>
            <button
              onClick={() => handleRemoveIngredientFromCart(ingredientName)}
              className="text-red-500 hover:text-red-700 text-sm font-medium"
            >
              Remove
            </button>
          </div>
        ) : (
          <button
            onClick={() => handleAddIngredientToCart(ingredientName)}
            className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-colors"
          >
            ADD TO CART
          </button>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-2xl font-semibold text-gray-600">Loading recipes...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Healthy Recipes</h1>
              <p className="text-gray-600 mt-2">Discover delicious and nutritious recipes for your diet plan</p>
            </div>
            <button
              onClick={() => navigate('/cart')}
              className="bg-green-500 text-white px-6 py-3 rounded-lg hover:bg-green-600 transition-colors font-medium"
            >
              View Cart ({cart?.count || 0})
            </button>
          </div>
        </div>
      </div>

      {/* Category Filter */}
      <div className="bg-white border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex space-x-4">
            {categories.map((category) => (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  selectedCategory === category
                    ? "bg-green-500 text-white"
                    : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                }`}
              >
                {category === "all" ? "All Recipes" : category}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Recipes Grid */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          {filteredRecipes.map((recipe) => (
            <div
              key={recipe._id}
              className="bg-white rounded-2xl shadow-lg p-6 hover:shadow-xl transition-shadow"
            >
              <div className="mb-4">
                <h3 className="text-2xl font-bold text-gray-900 mb-2">
                  {recipe.name}
                </h3>
                <p className="text-gray-600 mb-4">{recipe.description}</p>
                
                <div className="flex flex-wrap gap-2 mb-4">
                  <span className="flex items-center space-x-1 bg-gray-100 px-3 py-1 rounded-full text-sm">
                    <span>‚è∞</span>
                    <span>{recipe.cookingTime || "25 min"}</span>
                  </span>
                  <span className="flex items-center space-x-1 bg-gray-100 px-3 py-1 rounded-full text-sm">
                    <span>üë•</span>
                    <span>{recipe.servings || "2 servings"}</span>
                  </span>
                  <span className="flex items-center space-x-1 bg-gray-100 px-3 py-1 rounded-full text-sm">
                    <span>üî•</span>
                    <span>{recipe.calories || "420 cal"}</span>
                  </span>
                  <span className="flex items-center space-x-1 bg-gray-100 px-3 py-1 rounded-full text-sm">
                    <span>üìä</span>
                    <span>{recipe.difficulty || "Easy"}</span>
                  </span>
                </div>

                <div className="flex flex-wrap gap-2 mb-6">
                  {recipe.tags &&
                    recipe.tags.map((tag, index) => (
                      <span
                        key={index}
                        className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm font-medium"
                      >
                        {tag}
                      </span>
                    ))}
                </div>

                <div className="mb-6">
                  <h4 className="text-lg font-semibold text-gray-900 mb-3">
                    Ingredients
                  </h4>
                  {recipe.ingredients &&
                    recipe.ingredients
                      .filter(
                        (ingredient) =>
                          ingredient &&
                          (ingredient.productName || ingredient.name)
                      )
                      .map((ingredient) => renderIngredient(ingredient))}
                </div>

                <div className="flex space-x-4">
                  <button 
                    onClick={() => handleLearnMore(recipe._id)}
                    className="flex-1 border-2 border-green-500 text-green-500 px-6 py-3 rounded-lg hover:bg-green-500 hover:text-white transition-colors font-medium"
                  >
                    LEARN MORE
                  </button>
                  <button
                    onClick={() => handleOrderMissingIngredients(recipe)}
                    className="flex-1 bg-green-500 text-white px-6 py-3 rounded-lg hover:bg-green-600 transition-colors font-medium"
                  >
                    ORDER INGREDIENTS
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RecipesPage;
