import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../components/ui/card';
import { Button } from '../components/ui/button';
import { Badge } from '../components/ui/badge';
import { useCart } from '../contexts/CartContext';
import { productsAPI } from '../services/api';

export default function ProductDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart, getItemQuantity, isInCart } = useCart();

  const [product, setProduct] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadProduct();
  }, [id]);

  const loadProduct = async () => {
    try {
      setLoading(true);
      setError(null);

      const productData = await productsAPI.getById(id);
      setProduct(productData);

      // Load related products
      const related = await productsAPI.getByCategory(productData.category);
      setRelatedProducts(related.filter(p => p.id !== id).slice(0, 3));
    } catch (error) {
      console.error('Error loading product:', error);
      setError('Product not found');
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = async () => {
    if (!product.inStock) return;

    try {
      await addToCart(product.id, quantity);
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity >= 1 && newQuantity <= (product?.stockQuantity || 1)) {
      setQuantity(newQuantity);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted">Loading product...</p>
        </div>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="text-6xl mb-4">❌</div>
          <h3 className="text-xl font-semibold text-foreground mb-2">
            {error || 'Product not found'}
          </h3>
          <Button onClick={() => navigate('/products')}>
            Back to Products
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Background decoration */}
      <div className="absolute inset-0 bg-grid-pattern opacity-5"></div>
      <div className="absolute top-0 left-0 w-72 h-72 bg-primary/20 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob"></div>
      <div className="absolute top-0 right-0 w-72 h-72 bg-secondary/20 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-2000"></div>

      <div className="relative py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          {/* Breadcrumb */}
          <div className="mb-8">
            <Button
              variant="outline"
              onClick={() => navigate('/products')}
              className="mb-4"
            >
              ← Back to Products
            </Button>
          </div>

          {/* Product Details */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 mb-16">
            {/* Product Image */}
            <div className="space-y-6">
              <div className="bg-gradient-to-br from-gray-100 to-gray-200 rounded-3xl p-12 flex items-center justify-center">
                <span className="text-8xl">{product.image}</span>
              </div>

              {/* Stock Status */}
              <div className="text-center">
                {product.inStock ? (
                  <Badge variant="success" className="text-lg px-4 py-2">
                    ✓ In Stock ({product.stockQuantity} available)
                  </Badge>
                ) : (
                  <Badge variant="destructive" className="text-lg px-4 py-2">
                    ✗ Out of Stock
                  </Badge>
                )}
              </div>
            </div>

            {/* Product Info */}
            <div className="space-y-6">
              {/* Header */}
              <div>
                <div className="flex items-center space-x-2 mb-4">
                  <Badge variant="outline">{product.category}</Badge>
                  {product.tags?.map((tag, index) => (
                    <Badge key={index} variant="secondary">
                      {tag}
                    </Badge>
                  ))}
                </div>

                <h1 className="text-4xl font-bold text-foreground mb-4 font-heading">
                  {product.name}
                </h1>

                <p className="text-xl text-muted leading-relaxed mb-6">
                  {product.description}
                </p>
              </div>

              {/* Price */}
              <div className="space-y-2">
                <div className="flex items-center space-x-4">
                  <span className="text-4xl font-bold text-primary">
                    ₹{product.price}
                  </span>
                  {product.originalPrice && product.originalPrice > product.price && (
                    <span className="text-2xl text-muted line-through">
                      ₹{product.originalPrice}
                    </span>
                  )}
                </div>

                {product.originalPrice && product.originalPrice > product.price && (
                  <Badge variant="destructive" className="text-lg">
                    {Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100)}% OFF
                  </Badge>
                )}
              </div>

              {/* Rating */}
              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-1">
                  <span className="text-yellow-500 text-2xl">⭐</span>
                  <span className="text-xl font-semibold">
                    {product.rating} ({product.reviews} reviews)
                  </span>
                </div>
                <Badge variant="outline">{product.unit}</Badge>
              </div>

              {/* Supplier */}
              <div className="bg-gray-50 rounded-2xl p-4">
                <p className="text-sm text-muted mb-1">Supplier</p>
                <p className="font-semibold">{product.supplier}</p>
              </div>

              {/* Diet Compatibility */}
              <div>
                <h4 className="font-semibold text-foreground mb-3">Diet Compatibility</h4>
                <div className="flex flex-wrap gap-2">
                  {product.dietCompatible.map((diet) => (
                    <Badge key={diet} variant="outline">
                      {diet}
                    </Badge>
                  ))}
                </div>
              </div>

              {/* Add to Cart */}
              <div className="space-y-4">
                <div className="flex items-center space-x-4">
                  <label className="font-semibold">Quantity:</label>
                  <div className="flex items-center space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleQuantityChange(quantity - 1)}
                      disabled={quantity <= 1}
                    >
                      -
                    </Button>
                    <span className="w-12 text-center font-semibold">{quantity}</span>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleQuantityChange(quantity + 1)}
                      disabled={quantity >= (product.stockQuantity || 1)}
                    >
                      +
                    </Button>
                  </div>
                </div>

                <Button
                  variant="gradient"
                  size="lg"
                  className="w-full py-4 text-lg font-semibold"
                  onClick={handleAddToCart}
                  disabled={!product.inStock}
                >
                  {isInCart(product.id) 
                    ? `Update Cart (${getItemQuantity(product.id)})` 
                    : 'Add to Cart'
                  }
                </Button>
              </div>
            </div>
          </div>

          {/* Nutrition Information */}
          <Card className="mb-16">
            <CardHeader>
              <CardTitle className="text-2xl font-bold text-foreground font-heading">
                Nutrition Information
              </CardTitle>
              <CardDescription>
                Per {product.unit} serving
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
                <div className="text-center">
                  <div className="text-3xl font-bold text-primary mb-2">
                    {product.nutrition.calories}
                  </div>
                  <div className="text-muted">Calories</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold text-primary mb-2">
                    {product.nutrition.protein}
                  </div>
                  <div className="text-muted">Protein</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold text-primary mb-2">
                    {product.nutrition.fat}
                  </div>
                  <div className="text-muted">Fat</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold text-primary mb-2">
                    {product.nutrition.carbs}
                  </div>
                  <div className="text-muted">Carbs</div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Benefits */}
          <Card className="mb-16">
            <CardHeader>
              <CardTitle className="text-2xl font-bold text-foreground font-heading">
                Health Benefits
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {product.benefits.map((benefit, index) => (
                  <div key={index} className="flex items-center space-x-3">
                    <div className="w-3 h-3 bg-primary rounded-full"></div>
                    <span className="text-muted">{benefit}</span>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Related Products */}
          {relatedProducts.length > 0 && (
            <div className="mb-16">
              <h2 className="text-3xl font-bold text-foreground mb-8 font-heading">
                Related Products
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {relatedProducts.map((relatedProduct) => (
                  <Card
                    key={relatedProduct.id}
                    className="group hover:shadow-xl transition-all duration-300 hover:-translate-y-2 cursor-pointer"
                    onClick={() => navigate(`/product/${relatedProduct.id}`)}
                  >
                    <div className="h-32 bg-gradient-to-br from-gray-100 to-gray-200 flex items-center justify-center">
                      <span className="text-4xl">{relatedProduct.image}</span>
                    </div>
                    <CardHeader className="pb-3">
                      <CardTitle className="text-lg font-bold text-foreground font-heading">
                        {relatedProduct.name}
                      </CardTitle>
                      <CardDescription className="text-muted">
                        ₹{relatedProduct.price}
                      </CardDescription>
                    </CardHeader>
                  </Card>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
